select sa0170.pay_id,sa0170.pay_date,sa0170.user_id,sa0170.user_name,sa0170.shift,sa0170.pos_id,sa0170.review_status, T.store_cd,T.store_name,T.ofc,T.c_operatorname,sa0170.pay_in_amt,sa0170.create_user_id,sa0170.create_ymd,sa0170.create_hms,coalesce(customertable.customer_qty,0) customer_qty, (select COALESCE(sum(case pay_cd when '01' then pay_in_amt end),0) as pay_in_amt_0 from sa0180 where sa0170.pay_id=sa0180.pay_id),/*shift1*/ (select COALESCE(sum(case pay_cd when '02' then pay_in_amt end),0) as pay_in_amt_1 from sa0180 where sa0170.pay_id=sa0180.pay_id),/*shift2*/ (select COALESCE(sum(case pay_cd when '03' then pay_in_amt end),0) as pay_in_amt_2 from sa0180 where sa0170.pay_id=sa0180.pay_id),/*shift3*/ (select COALESCE(sum(case pay_cd when '04' then pay_in_amt end),0) as pay_in_amt_3 from sa0180 where sa0170.pay_id=sa0180.pay_id),/*Additional*/ (select COALESCE(sum(case pay_cd when '05' then pay_in_amt end),0) as pay_in_amt_4 from sa0180 where sa0170.pay_id=sa0180.pay_id) /*Offset Claim*/ from sa0170 left join ( SELECT coalesce(sum(a.sale_cnt),0) customer_qty,a.acc_date,a.store_cd FROM (SELECT distinct sale_cnt,to_char(tran_date::date-1,'yyyyMMdd') acc_date ,store_cd FROM sa0090 ) a WHERE 1=1 GROUP BY a.acc_date,a.store_cd ) AS customertable on sa0170.store_cd=customertable.store_cd and sa0170.pay_date = customertable.acc_date, (SELECT DISTINCT MA1000.store_cd,MA1000.store_name,MA1000.ofc,t_operator.c_operatorcode,t_operator.c_operatorname from MA1000,SA0170,t_operator WHERE SA0170.store_cd=MA1000.STORE_CD AND MA1000.ofc=t_operator.c_operatorcode ) AS T where sa0170.store_cd IN
#{store,jdbcType=VARCHAR}
and sa0170.pay_sts='10' and T.store_cd=sa0170.store_cd
and sa0170.pay_date >= #{vStartDate,jdbcType=VARCHAR}
and sa0170.pay_date <= #{vEndDate,jdbcType=VARCHAR}
AND T.c_operatorcode=#{am,jdbcType=VARCHAR}
order by pay_date,user_id
LIMIT ${rows} OFFSET ${limitStart}

