select t1.amount+t2.amount pay_amt, pay_0.pay_amt_0+pay1_0.pay_amt_0 pay_amt_0, pay_1.pay_amt_1+pay1_1.pay_amt_1 pay_amt_1, pay_2.pay_amt_2+pay1_2.pay_amt_2 pay_amt_2, pay_hht_0.pay_amt_0 pay_hht_amt0, pay_hht_1.pay_amt_1 pay_hht_amt1, pay_hht_2.pay_amt_2 pay_hht_amt2 from (select COALESCE ( SUM ( CASE WHEN pay_cd1 = '01' THEN pay_amount1 - change_amount WHEN pay_cd2 = '01' THEN pay_amount2 - change_amount WHEN pay_cd3 = '01' THEN pay_amount3 - change_amount WHEN pay_cd4 = '01' THEN pay_amount4 - change_amount END ), 0 ) amount from sa0010
) t1, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_0 from sa0010
/*--早班*/
and to_number(to_char(tran_date,'hh24'),'99') >= 6 and to_number(to_char(tran_date,'hh24'),'99') < 14
) pay_0, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_1 from sa0010
/*--中班*/
and to_number(to_char(tran_date,'hh24'),'99') >= 14 and to_number(to_char(tran_date,'hh24'),'99') < 22
) pay_1, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_2 from sa0010
/*--晚班*/
and (to_number(to_char(tran_date,'hh24'),'99') < 6 or to_number(to_char(tran_date,'hh24'),'99') >= 22)
) pay_2, /* HHT的销售数据 */ (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_0 from sa0010
and pos_id = '99' /*--早班*/
and to_number(to_char(tran_date,'hh24'),'99') >= 6 and to_number(to_char(tran_date,'hh24'),'99') < 14
) pay_hht_0, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_1 from sa0010
and pos_id = '99' /*--中班*/
and to_number(to_char(tran_date,'hh24'),'99') >= 14 and to_number(to_char(tran_date,'hh24'),'99') < 22
) pay_hht_1, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_2 from sa0010
and pos_id = '99' /*--晚班*/
and (to_number(to_char(tran_date,'hh24'),'99') < 6 or to_number(to_char(tran_date,'hh24'),'99') >= 22)
) pay_hht_2, (select COALESCE(sum(sa0030.sale_amount-sa0030.over_amount+sa0030.spill_amount),0) amount from sa0030
) t2, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_0 from sa0030
/*--早班*/
and to_number(to_char(tran_date,'hh24'),'99') >= 6 and to_number(to_char(tran_date,'hh24'),'99') < 14
) pay1_0, (select COALESCE(sum(case when pay_cd1='01' then pay_amount1-COALESCE(change_amount,0) when pay_cd2='01' then pay_amount2-COALESCE(change_amount,0) when pay_cd3='01' then pay_amount3-COALESCE(change_amount,0) when pay_cd4='01' then pay_amount4-COALESCE(change_amount,0) end),0) as pay_amt_1 from sa0030
/*--中班*/
