mapper.select:
SELECT DISTINCT COUNT(*) FROM
sa0010,sa0020 where 1=1 and sa0010.acc_date = sa0020.acc_date and sa0010.store_cd = sa0020.store_cd and sa0010.pos_id = sa0020.pos_id and sa0010.tran_serial_no = sa0020.tran_serial_no and ( lower(sa0020.article_id) like lower(CONCAT('%',#{barcode,jdbcType=VARCHAR},'%')) or lower(sa0020.barcode) like lower(CONCAT('%',#{barcode,jdbcType=VARCHAR},'%')) )
sa0010,sa0020,ma1100 where 1=1 and sa0020.article_id = ma1100.article_id
AND ma1100.effective_start_date <= sa0010.acc_date AND ma1100.effective_end_date >= sa0010.acc_date
and sa0010.acc_date = sa0020.acc_date and sa0010.store_cd = sa0020.store_cd and sa0010.pos_id = sa0020.pos_id and sa0010.tran_serial_no = sa0020.tran_serial_no and( lower(ma1100.article_short_name) like lower(CONCAT('%',#{articleShortName,jdbcType=VARCHAR},'%')) )
sa0010 where 1=1
and sa0010.store_cd IN
#{store,jdbcType=VARCHAR}
and to_char(sa0010.tran_date,'YYYYMMDD') >= #{startDate,jdbcType=VARCHAR}
and to_char(sa0010.tran_date,'YYYYMMDD') <= #{endDate,jdbcType=VARCHAR}
and sa0010.cashier_id = #{cashierId,jdbcType=VARCHAR}
and sa0010.member_id = #{memberId,jdbcType=VARCHAR}
and sa0010.pos_id = #{posId,jdbcType=VARCHAR}
and sa0010.sale_amount ${calType} #{payAmt,jdbcType=NUMERIC}
and ( (sa0010.pay_cd1 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount1 ${calType} #{payAmt,jdbcType=NUMERIC}) or (sa0010.pay_cd2 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount2 ${calType} #{payAmt,jdbcType=NUMERIC}) or (sa0010.pay_cd3 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount3 ${calType} #{payAmt,jdbcType=NUMERIC}) or (sa0010.pay_cd4 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount4 ${calType} #{payAmt,jdbcType=NUMERIC}))
and (sa0010.sale_amount - sa0010.over_amount) ${calType} #{payAmt,jdbcType=NUMERIC}
/*--早班*/
and tran_date >= to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 06:00:00','yyyyMMdd hh24:mi:ss') and tran_date < to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 14:00:00','yyyyMMdd hh24:mi:ss')
/*--中班*/
and tran_date >= to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 14:00:00','yyyyMMdd hh24:mi:ss') and tran_date < to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 22:00:00','yyyyMMdd hh24:mi:ss')
/*--晚班*/
and (tran_date < to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 06:00:00','yyyyMMdd hh24:mi:ss') or tran_date >= to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 22:00:00','yyyyMMdd hh24:mi:ss'))
and sa0010.tran_type = 'sale'

select count(1) from (SELECT DISTINCT sa0010.acc_date, sa0010.store_cd, sa0010.pos_id, sa0010.tran_serial_no, to_char(sa0010.tran_date,'YYYYMMDD') AS tran_date, to_char(tran_date,'HH24:MI:SS') AS tran_time, case when
sa0010.tran_date >= to_timestamp(sa0010.acc_date||' 06:00:00','yyyyMMdd hh24:mi:ss') and sa0010.tran_date < to_timestamp(sa0010.acc_date||' 14:00:00','yyyyMMdd hh24:mi:ss') then 'shift1' when tran_date >= to_timestamp(sa0010.acc_date||' 14:00:00','yyyyMMdd hh24:mi:ss') and tran_date < to_timestamp(sa0010.acc_date||' 22:00:00','yyyyMMdd hh24:mi:ss') then 'Shift2' when (tran_date < to_timestamp(sa0010.acc_date||' 06:00:00','yyyyMMdd hh24:mi:ss') or tran_date >= to_timestamp(sa0010.acc_date||' 22:00:00','yyyyMMdd hh24:mi:ss')) then 'Shift3' end As shift, sa0010.sale_serial_no, sa0010.cashier_id, sa0010.member_id, sa0010.sale_amount, sa0010.over_amount, sa0010.pay_cd1, sa0010.pay_cd2, sa0010.pay_cd3, sa0010.pay_cd4, sa0010.pay_amount1, sa0010.pay_amount2, sa0010.pay_amount3, sa0010.pay_amount4, (coalesce(sa0010.sale_amount,0) - coalesce(sa0010.over_amount,0)) AS pay_amount FROM
sa0010,sa0020 where 1=1 and sa0010.acc_date = sa0020.acc_date and sa0010.store_cd = sa0020.store_cd and sa0010.pos_id = sa0020.pos_id and sa0010.tran_serial_no = sa0020.tran_serial_no and ( lower(sa0020.article_id) like lower(CONCAT('%',#{barcode,jdbcType=VARCHAR},'%')) or lower(sa0020.barcode) like lower(CONCAT('%',#{barcode,jdbcType=VARCHAR},'%')) )
sa0010,sa0020,ma1100 where 1=1 and sa0020.article_id = ma1100.article_id
AND ma1100.effective_start_date <= sa0010.acc_date AND ma1100.effective_end_date >= sa0010.acc_date
and sa0010.acc_date = sa0020.acc_date and sa0010.store_cd = sa0020.store_cd and sa0010.pos_id = sa0020.pos_id and sa0010.tran_serial_no = sa0020.tran_serial_no and( lower(ma1100.article_short_name) like lower(CONCAT('%',#{articleShortName,jdbcType=VARCHAR},'%')) )
sa0010 where 1=1
and sa0010.store_cd IN
#{store,jdbcType=VARCHAR}
and sa0010.tran_date >= TO_TIMESTAMP(#{startDate,jdbcType=VARCHAR}|| ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS')
and (sa0010.tran_date - INTERVAL'1 day') < TO_TIMESTAMP(#{endDate,jdbcType=VARCHAR} || ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS')
and sa0010.cashier_id = #{cashierId,jdbcType=VARCHAR}
and sa0010.member_id = #{memberId,jdbcType=VARCHAR}
and cast(sa0010.sale_serial_no as VARCHAR) like concat('%',#{saleSerialNo,jdbcType=VARCHAR},'%')
and sa0010.pos_id = #{posId,jdbcType=VARCHAR}
and sa0010.sale_amount ${calType} #{payAmt,jdbcType=NUMERIC}
and ( (sa0010.pay_cd1 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount1 ${calType} #{payAmt,jdbcType=NUMERIC}) or (sa0010.pay_cd2 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount2 ${calType} #{payAmt,jdbcType=NUMERIC}) or (sa0010.pay_cd3 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount3 ${calType} #{payAmt,jdbcType=NUMERIC}) or (sa0010.pay_cd4 = #{payType,jdbcType=VARCHAR} and sa0010.pay_amount4 ${calType} #{payAmt,jdbcType=NUMERIC}))
and (sa0010.sale_amount - sa0010.over_amount) ${calType} #{payAmt,jdbcType=NUMERIC}
/*--早班*/
and tran_date >= to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 06:00:00','yyyyMMdd hh24:mi:ss') and tran_date < to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 14:00:00','yyyyMMdd hh24:mi:ss')
/*--中班*/
and tran_date >= to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 14:00:00','yyyyMMdd hh24:mi:ss') and tran_date < to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 22:00:00','yyyyMMdd hh24:mi:ss')
/*--晚班*/
and (tran_date < to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 06:00:00','yyyyMMdd hh24:mi:ss') or tran_date >= to_timestamp(to_char(sa0010.tran_date,'yyyyMMdd')||' 22:00:00','yyyyMMdd hh24:mi:ss'))
and sa0010.tran_type = 'sale'
order by sa0010.acc_date,sa0010.pos_id,sa0010.tran_serial_no ASC) as A

SELECT sa0000.pay_cd,
        sa0000.pay_name_print,
        (case when sa0000.pay_cd = sa0010.pay_cd1 then sa0010.pay_amount1-sa0010.change_amount
        when sa0000.pay_cd = sa0010.pay_cd2 then sa0010.pay_amount2
        when sa0000.pay_cd = sa0010.pay_cd3 then sa0010.pay_amount3
        when sa0000.pay_cd = sa0010.pay_cd4 then sa0010.pay_amount4
        else 0 end) AS pay_amount
    FROM sa0010,sa0000
    WHERE sa0010.acc_date = #{accDate,jdbcType=VARCHAR}
        AND sa0010.store_cd = #{storeCd,jdbcType=VARCHAR}
        AND sa0010.pos_id = #{posId,jdbcType=VARCHAR}
        AND sa0010.tran_serial_no = #{tranSerialNo,jdbcType=INTEGER}
    ORDER BY sa0000.pay_cd ASC

SELECT sa0020.acc_date accDate, sa0020.store_cd storeCd, sa0020.pos_id posId, sa0020.tran_serial_no tranSerialNo, sa0020.sale_seq_no saleSeqNo, sa0020.barcode, ma1100.article_short_name articleShortName, ma1100.spec, ma0220.unit_name unitName, sa0020.price_original priceOriginal, sa0020.price_actual priceActual, sa0020.sale_qty saleQty, sa0020.sale_amount saleAmount, sa0020.discount_amount discountAmount FROM sa0020 left join ma1100 on sa0020.article_id = ma1100.article_id
AND ma1100.effective_start_date <= #{accDate,jdbcType=VARCHAR} AND ma1100.effective_end_date >= #{accDate,jdbcType=VARCHAR}
left join ma0220 on ma1100.sales_unit_id = ma0220.unit_id AND ma0220.effective_sts = '10' WHERE sa0020.acc_date = #{accDate,jdbcType=VARCHAR} AND sa0020.store_cd = #{storeCd,jdbcType=VARCHAR} AND sa0020.pos_id = #{posId,jdbcType=VARCHAR} AND sa0020.tran_serial_no = #{tranSerialNo,jdbcType=INTEGER} /*AND sa0020.detail_type = 's' 除了免费商品 */ AND sa0020.detail_type in ('s','m','r','ms','ra','fg','mbn','mbs') ORDER BY sale_seq_no ASC

SELECT sa0020.acc_date accDate, sa0020.store_cd storeCd, sa0020.pos_id posId, sa0020.tran_serial_no tranSerialNo, sa0020.sale_seq_no saleSeqNo, sa0020.barcode, ma1100.article_short_name articleShortName, ma1100.spec, ma0220.unit_name unitName, sa0020.price_original priceOriginal, sa0020.price_actual priceActual, sa0020.sale_qty saleQty, sa0020.sale_amount saleAmount, sa0020.discount_amount discountAmount FROM sa0020 left join ma1100 on sa0020.article_id = ma1100.article_id
AND ma1100.effective_start_date <= #{accDate,jdbcType=VARCHAR} AND ma1100.effective_end_date >= #{accDate,jdbcType=VARCHAR}
left join ma0220 on ma1100.sales_unit_id = ma0220.unit_id AND ma0220.effective_sts = '10' WHERE sa0020.acc_date = #{accDate,jdbcType=VARCHAR} AND sa0020.store_cd = #{storeCd,jdbcType=VARCHAR} AND sa0020.pos_id = #{posId,jdbcType=VARCHAR} AND sa0020.tran_serial_no = #{tranSerialNo,jdbcType=INTEGER} /*AND sa0020.detail_type = 's'*/ AND sa0020.detail_type in ('s','m','r','ms','ra','fg','mbn','mbs')
ORDER BY sale_seq_no ASC
LIMIT ${rows} OFFSET ${limitStart};

SELECT count(*)
 FROM sa0020,ma1100,ma0220
 WHERE sa0020.article_id = ma1100.article_id
 AND ma1100.sales_unit_id = ma0220.unit_id

 AND ma1100.effective_start_date <= #{accDate,jdbcType=VARCHAR}
 AND ma1100.effective_end_date >= #{accDate,jdbcType=VARCHAR}

 AND sa0020.acc_date = #{accDate,jdbcType=VARCHAR}
 AND sa0020.store_cd = #{storeCd,jdbcType=VARCHAR}
 AND sa0020.pos_id = #{posId,jdbcType=VARCHAR}
 AND sa0020.tran_serial_no = #{tranSerialNo,jdbcType=INTEGER}
 AND sa0020.detail_type in ('s','m','r','ms','ra','fg','mbn','mbs')
 AND ma0220.effective_sts = '10'
