SELECT A.*, MA0070.dep_name, MA0080.pma_name, MA0090.category_name, MA1000.store_name, t_operator.c_operatorname, MA0100.sub_category_name FROM ( SELECT MA1000.ofc, MA1100.article_name, MA1100.dep_cd, MA1100.pma_cd, MA1100.sub_category_cd, MA1100.category_cd, SA0500.store_cd, sa0500.sale_qty, sa0500.acc_date, sa0500.sale_amt, sa0500.barcode, sa0500.seq_no, SA0500.article_id, sa0500.region_cd FROM SA0500 LEFT JOIN MA1100 ON MA1100.article_id = SA0500.article_id left join MA1000 ON MA1000.store_cd= SA0500.store_cd ) A, MA0070, MA0080, MA0090, MA1000, t_operator, MA0100 WHERE A.dep_cd = MA0070.dep_cd AND A.store_cd=MA1000.store_cd and MA1000.store_cd IN
#{store,jdbcType=VARCHAR}
AND A.pma_cd = ma0080.pma_cd AND A.category_cd = MA0090.category_cd AND A.sub_category_cd = MA0100.sub_category_cd AND t_operator.c_operatorcode=A.ofc
AND A.region_cd=#{param.regionCd}
AND A.ofc=#{param.amCd}
AND A.acc_date >= #{param.startDate}
AND A.acc_date <= #{param.endDate}
AND A.dep_cd = #{param.depCd}
AND A.pma_cd = #{param.pmaCd}
AND A.category_cd = #{param.categoryCd}
AND A.sub_category_cd = #{param.subCategoryCd}

SELECT A.*, concat(ma0070.dep_cd,' ',ma0070.dep_name) dep_name, concat(ma0080.pma_cd,' ',ma0080.pma_name) pma_name, concat(ma0090.category_cd,' ',ma0090.category_name) category_name, concat(ma0100.sub_category_cd,' ',ma0100.sub_category_name) sub_category_name, MA1000.store_name, t_operator.c_operatorname, MA0100.sub_category_name FROM ( SELECT MA1000.ofc, MA1100.article_name, MA1100.dep_cd, MA1100.pma_cd, MA1100.sub_category_cd, MA1100.category_cd, sa0510.city_cd, SA0510.store_cd, sa0510.sale_qty, sa0510.acc_date, sa0510.sale_amt, sa0510.barcode, sa0510.seq_no, SA0510.article_id FROM SA0510 LEFT JOIN MA1100 ON MA1100.article_id = SA0510.article_id left join MA1000 ON MA1000.store_cd=SA0510.store_cd ) A, MA0070, MA0080, MA0090, MA1000, t_operator, MA0100 WHERE A.dep_cd = MA0070.dep_cd AND A.store_cd=MA1000.store_cd and MA1000.store_cd IN
#{store,jdbcType=VARCHAR}
AND A.pma_cd = ma0080.pma_cd AND A.category_cd = MA0090.category_cd AND A.sub_category_cd = MA0100.sub_category_cd AND t_operator.c_operatorcode=A.ofc
AND A.city_cd=#{param.cityCd}
AND A.ofc=#{param.amCd}
AND A.acc_date >= #{param.startDate}
AND A.acc_date <= #{param.endDate}
AND A.dep_cd = #{param.depCd}
AND A.pma_cd = #{param.pmaCd}
AND A.category_cd = #{param.categoryCd}
AND A.sub_category_cd = #{param.subCategoryCd}

