mapper.select:
SELECT a.psd, a.store_cd, a.store_name, SUM(A.avg_customer_no) as avg_customer, SUM(A.sale_amt) as sale_amt_sum, to_char(A.sale_date,'dd/mm/yyyy') AS every_sale_date, a.c_operatorcode, a.c_operatorname FROM ( select distinct sa02.sale_date, ma10.store_name, d1.avg_customer_no, d1.sale_amt, ia1.psd, ma10.store_cd, oper.c_operatorcode, oper.c_operatorname from ia0010 ia1 left JOIN ma1000 ma10 ON ia1.store_cd=ma10.store_cd, sa0020 sa02, cm9060 c1, in0010 d1, t_operator oper where ma10.store_cd=ia1.store_cd AND sa02.store_cd=ia1.store_cd AND ia1.store_cd= d1.store_cd AND c1.sp_id = '0000' AND c1.sp_value BETWEEN ma10.effective_start_date AND ma10.effective_end_date AND oper.c_operatorcode = ma10.ofc AND ma10.store_cd IN
#{store,jdbcType=VARCHAR}
AND ma10.store_name=#{daySaleReportParamdto.storeName}
AND to_char(sa02.sale_date,'yyyyMMdd') between #{daySaleReportParamdto.effectiveStartDate} and #{daySaleReportParamdto.effectiveEndDate}
AND to_char(sa02.sale_date,'yyyyMMdd') between #{daySaleReportParamdto.effectiveStartDate} and ma10.effective_end_date
AND ma10.ofc = #{daySaleReportParamdto.am}
) A GROUP BY a.store_name, a.store_cd, a.c_operatorcode, a.c_operatorname, to_char(A.sale_date,'dd/mm/yyyy'), a.psd

select count(two.*) from (select store_cd,store_name,acc_date,c_operatorcode,c_operatorname, sum(customer_count) customer_count, max(case two_hours when '6h-8h' then sales_amt else 0 end) as "6h-8h", max(case two_hours when '8h-10h' then sales_amt else 0 end) as "8h-10h", max(case two_hours when '10h-12h' then sales_amt else 0 end) as "10h-12h", max(case two_hours when '12h-14h' then sales_amt else 0 end) as "12h-14h", max(case two_hours when '14h-16h' then sales_amt else 0 end) as "14h-16h", max(case two_hours when '16h-18h' then sales_amt else 0 end) as "16h-18h", max(case two_hours when '18h-20h' then sales_amt else 0 end) as "18h-20h", max(case two_hours when '20h-22h' then sales_amt else 0 end) as "20h-22h", max(case two_hours when '22h-24h' then sales_amt else 0 end) as "22h-24h", max(case two_hours when '0h-2h' then sales_amt else 0 end) as "0h-2h", max(case two_hours when '2h-4h' then sales_amt else 0 end) as "2h-4h", max(case two_hours when '4h-6h' then sales_amt else 0 end) as "4h-6h" from (SELECT B.*, T.c_operatorname,T.c_operatorcode,T1.code_name as two_hours FROM (SELECT MA1000.STORE_NAME, IA0010.acc_date, IA0010.store_cd, IA0010.hours, IA0010.customer_count, IA0010.SALES_AMT, IA0010.PSD FROM ia0010 LEFT JOIN MA1000 ON IA0010.STORE_CD = MA1000.STORE_CD) AS B LEFT JOIN (SELECT t_operator.c_operatorname,t_operator.c_operatorcode,MA1000.STORE_CD FROM t_operator,MA1000 WHERE MA1000.OFC=t_operator.c_operatorcode) AS T ON T.STORE_CD=B.STORE_CD LEFT JOIN (select code_type,code_value,code_name from cm9010 where code_type = '00840' and effective_sts = '10') T1 on B.hours = T1.code_value where 1 =1 AND B.store_cd In
#{store,jdbcType=VARCHAR}
AND B.acc_date between #{daySaleReportParamdto.effectiveStartDate} and #{daySaleReportParamdto.effectiveEndDate}
AND T.c_operatorcode = #{daySaleReportParamdto.am}
LIMIT ${daySaleReportParamdto.rows} OFFSET ${daySaleReportParamdto.limitStart}
) as one group by store_cd,store_name,acc_date,c_operatorcode,c_operatorname ) as two

select store_cd,store_name,acc_date,c_operatorcode,c_operatorname,customer_count, "6h-8h","8h-10h","10h-12h","12h-14h",("6h-8h"+"8h-10h"+"10h-12h"+"12h-14h") as shift1, "14h-16h","16h-18h","18h-20h","20h-22h",("14h-16h"+"16h-18h"+"18h-20h"+"20h-22h") as shift2, "22h-24h","0h-2h","2h-4h","4h-6h",("22h-24h"+"0h-2h"+"2h-4h"+"4h-6h") as shift3, ("6h-8h"+"8h-10h"+"10h-12h"+"12h-14h"+"14h-16h"+"16h-18h"+"18h-20h"+"20h-22h"+"22h-24h"+"0h-2h"+"2h-4h"+"4h-6h") as total_amt from (select store_cd,store_name,acc_date,c_operatorcode,c_operatorname, sum(customer_count) customer_count, max(case two_hours when '6h-8h' then sales_amt else 0 end) as "6h-8h", max(case two_hours when '8h-10h' then sales_amt else 0 end) as "8h-10h", max(case two_hours when '10h-12h' then sales_amt else 0 end) as "10h-12h", max(case two_hours when '12h-14h' then sales_amt else 0 end) as "12h-14h", max(case two_hours when '14h-16h' then sales_amt else 0 end) as "14h-16h", max(case two_hours when '16h-18h' then sales_amt else 0 end) as "16h-18h", max(case two_hours when '18h-20h' then sales_amt else 0 end) as "18h-20h", max(case two_hours when '20h-22h' then sales_amt else 0 end) as "20h-22h", max(case two_hours when '22h-24h' then sales_amt else 0 end) as "22h-24h", max(case two_hours when '0h-2h' then sales_amt else 0 end) as "0h-2h", max(case two_hours when '2h-4h' then sales_amt else 0 end) as "2h-4h", max(case two_hours when '4h-6h' then sales_amt else 0 end) as "4h-6h" from (SELECT B.*,T.c_operatorname,T.c_operatorcode,T1.code_name as two_hours FROM (SELECT MA1000.STORE_NAME, IA0010.acc_date, IA0010.store_cd, IA0010.hours, IA0010.customer_count, IA0010.SALES_AMT, IA0010.PSD FROM ia0010 LEFT JOIN MA1000 ON IA0010.STORE_CD = MA1000.STORE_CD) AS B LEFT JOIN (SELECT t_operator.c_operatorname,t_operator.c_operatorcode,MA1000.STORE_CD FROM t_operator,MA1000 WHERE MA1000.OFC=t_operator.c_operatorcode) AS T ON T.STORE_CD=B.STORE_CD LEFT JOIN (select code_type,code_value,code_name from cm9010 where code_type = '00840' and effective_sts = '10') T1 on B.hours = T1.code_value where 1 =1 AND B.store_cd In
#{store,jdbcType=VARCHAR}
AND B.acc_date between #{daySaleReportParamdto.effectiveStartDate} and #{daySaleReportParamdto.effectiveEndDate}
AND T.c_operatorcode = #{daySaleReportParamdto.am}
LIMIT ${daySaleReportParamdto.rows} OFFSET ${daySaleReportParamdto.limitStart}
) as one group by store_cd,store_name,acc_date,c_operatorcode,c_operatorname ) as two
