SELECT DISTINCT ma10.store_cd, ma10.store_name, ma07.dep_cd, concat(ma07.dep_cd,' ',ma07.dep_name) dep_name, ma08.pma_cd, concat(ma08.pma_cd,' ',ma08.pma_name) pma_name, ma09.category_cd, concat(ma09.category_cd,' ',ma09.category_name) category_name, ma01.sub_category_cd, concat(ma01.sub_category_cd,' ',ma01.sub_category_name) sub_category_name, sa02.sale_date, sa01.tran_date, sa02.price_actual, sa02.sale_qty, sa02.sale_amount, ma11.article_id, ma11.article_name, sa02.barcode, sa02.acc_date, oper.c_operatorcode, oper.c_operatorname FROM ma1000 ma10, sa0010 sa01, sa0020 sa02, ma1100 ma11, ma0070 ma07, ma0080 ma08, ma0090 ma09, ma0100 ma01, t_operator oper where ma10.store_cd=sa02.store_cd AND ma10.ofc = oper.c_operatorcode and (SELECT C.sp_value FROM CM9060 C WHERE C.sp_id = '0000') BETWEEN ma11.effective_start_date AND ma11.effective_end_date and (SELECT C.sp_value FROM CM9060 C WHERE C.sp_id = '0000') BETWEEN ma10.effective_start_date AND ma10.effective_end_date AND sa02.article_id=ma11.article_id AND ma11.pma_cd=ma08.pma_cd AND ma11.dep_cd = ma07.dep_cd AND ma11.category_cd = ma09.category_cd AND ma11.sub_category_cd = ma01.sub_category_cd AND ma08.effective_sts = '10' AND ma10.store_cd IN
#{store,jdbcType=VARCHAR}
AND sa01.acc_date = sa02.acc_date AND sa01.store_cd = sa02.store_cd AND sa01.pos_id = sa02.pos_id AND sa01.tran_serial_no = sa02.tran_serial_no
AND sa01.tran_date >= TO_TIMESTAMP(#{goodSaleReport.startDate,jdbcType=VARCHAR}|| ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS')
and (sa01.tran_date - INTERVAL'1 day') < TO_TIMESTAMP(#{goodSaleReport.endDate,jdbcType=VARCHAR} || ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS')
AND lower( sa02.barcode ) LIKE CONCAT('%',#{goodSaleReport.barcode},'%')
AND ma11.dep_cd = #{goodSaleReport.depCd}
AND ma11.pma_cd = #{goodSaleReport.pmaCd}
and ma11.category_cd = #{goodSaleReport.categoryCd,jdbcType=VARCHAR}
and ma11.sub_category_cd = #{goodSaleReport.subCategoryCd,jdbcType=VARCHAR}
AND ma10.zo_cd = #{goodSaleReport.city}
AND ma10.ofc = #{goodSaleReport.am}
AND concat(lower(ma11.article_name),'',lower(ma11.article_id),'',lower(ma11.article_name_en),'',lower(ma11.article_short_name_en)) like lower(CONCAT('%',#{goodSaleReport.articleName},'%'))
LIMIT ${goodSaleReport.rows} OFFSET ${goodSaleReport.limitStart}

