mapper.select:
SELECT DISTINCT T.*, T1.article_name, T1.spec, T1.sales_unit_id, T2.store_name, T1.dep_cd, T1.pma_cd, T1.category_cd, T1.sub_category_cd, T3.barcode FROM ( SELECT A.store_cd, A.acc_date, B.parent_article_id AS article_id, COALESCE(A.sale_cost_t,0) AS sale_cost_t, COALESCE(A.sale_qty_t,0) AS sale_qty_t, COALESCE(A.sale_amt_t,0) AS sale_amt_t, COALESCE(A.return_qty_t,0) AS return_qty_t, COALESCE(A.return_amt_t,0) AS return_amt_t, COALESCE(A.avg_cost_notax,0) AS avg_cost_notax, COALESCE(A.group_sale_cost,0) AS group_sale_cost, COALESCE((A.sale_amt_t - A.sale_amt_notax_t),0) AS sale_tax_t, COALESCE((A.return_amt_t - A.return_amt_notax_t),0) AS return_tax_t, COALESCE((A.sale_amt_t - A.sale_cost_t),0) AS gross_margin, CASE WHEN A.sale_cost_t = 0 OR A.sale_cost_t IS NULL THEN 0 ELSE ROUND(( A.sale_amt_t - A.sale_cost_t ) / A.sale_cost_t, 4 ) END AS gross_margin_rate, C.vendor_id FROM SK3000 A, MA1200 B, ma2240 C WHERE A.article_id = B.parent_article_id AND A.store_cd = C.store_cd AND B.parent_article_id = C.article_id
AND A.store_cd = #{storeCd,jdbcType=VARCHAR}
) T LEFT JOIN ( SELECT article_id, effective_start_date, article_name, spec, sales_unit_id,dep_cd,pma_cd,category_cd,sub_category_cd FROM MA1100 WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T1 ON T.article_id = T1.article_id LEFT JOIN ( SELECT store_cd, effective_start_date, store_name FROM MA1000 WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T2 ON T.store_cd = T2.store_cd LEFT JOIN ( SELECT article_id,default_barcode barcode,vendor_id from ma1110 where #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date and structure_cd = (SELECT ma2.admin_structure_cd FROM ma0020 ma2 WHERE ma2.structure_cd = (SELECT ma1.admin_structure_cd FROM ma0020 ma1 WHERE ma1.structure_cd = #{storeCd,jdbcType=VARCHAR})) ) T3 ON T.article_id = T3.article_id AND T3.vendor_id = T.vendor_id ORDER BY T.acc_date DESC, T.article_id
