mapper.select:
SELECT DISTINCT f.*, d.vendor_id, g.default_barcode AS barcode, e.vendor_name FROM ( SELECT DISTINCT a.item_code, c.article_name, a.whs_code AS store_cd, b.store_name , SUM(A.qty) AS qty , to_char(a.ord_date, 'yyyymmdd') AS ord_date , to_char(a.upload_time, 'yyyyMMddhh24miss') AS upload_time FROM hht_purchase_order_upload_his a LEFT JOIN ma1000 b ON a.whs_code = b.store_cd LEFT JOIN ma1100 c ON a.item_code = c.article_id WHERE 1 = 1 AND a.whs_code = b.store_cd
and ( LOWER (c.article_id) like lower(CONCAT('%', #{param.articleId,jdbcType=VARCHAR},'%')) or LOWER (c.article_name) like lower(CONCAT('%', #{param.articleId,jdbcType=VARCHAR},'%')) )
and to_char(a.ord_date,'yyyymmdd') >= #{param.orderStartDate,jdbcType=VARCHAR}
and to_char(a.ord_date,'yyyymmdd') <= #{param.orderEndDate,jdbcType=VARCHAR}
and a.dc='Y'
and a.dc='N'
GROUP BY a.item_code, a.whs_code, b.store_name, a.ord_date, upload_time, C.article_name ) f, ma2240 d, ma2000 e, ma1110 g WHERE 1 = 1 AND f.item_code = d.article_id
and lower (e.vendor_id ) like lower(CONCAT('%', #{param.vendorId,jdbcType=VARCHAR},'%'))
and f.store_cd= #{param.storeCd,jdbcType=VARCHAR}
and lower (g.default_barcode) like lower(CONCAT('%', #{param.barcode,jdbcType=VARCHAR},'%'))
AND d.store_cd = f.store_cd AND d.vendor_id = e.vendor_id AND g.vendor_id = d.vendor_id AND g.article_id = f.item_code AND g.structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE 1=1 AND structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = f.store_cd AND effective_sts = '10' ) AND effective_sts = '10' )
AND f.ord_date between e.effective_start_date and e.effective_end_date AND f.ord_date between g.effective_start_date and g.effective_end_date
LIMIT ${param.rows} OFFSET ${param.limitStart}
