mapper.select:
SELECT a.*,B.*,c.* ,d.*,e.store_name
from
ma1000 e right join
(select store_cd,   COALESCE(count(store_cd),0) AS total_open_po from od0000  where
1=1

    and  store_cd=#{param.storeCd}

and store_cd in

    #{store,jdbcType=VARCHAR}

and

order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date AND  order_sts !='01'  GROUP BY store_cd) a
on e.store_cd=a.store_cd
left join
(select store_cd, COALESCE(count(store_cd),0) as no_of_confi_po from od0000  where 1=1

    and  store_cd=#{param.storeCd}

and store_cd in

    #{store,jdbcType=VARCHAR}

and   order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date    AND  review_status='20'  GROUP BY store_cd)  b
on a.store_cd=b.store_cd  left join
(select store_cd,  COALESCE(count(store_cd),0)  as  no_of_pending_grpo from od0000  where 1=1

    and  store_cd=#{param.storeCd}

and store_cd in

    #{store,jdbcType=VARCHAR}

 and   order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date     AND  review_status='15'  GROUP BY store_cd) c
 on b.store_cd=c.store_cd left join
(select store_cd, COALESCE(count(store_cd),0)  AS grpo_of_qty from od0000  where 1=1

    and  store_cd=#{param.storeCd}

and store_cd in

    #{store,jdbcType=VARCHAR}

and    order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date
AND  review_status='10'  GROUP BY store_cd)
d  on d.store_cd=c.store_cd

    and  c.store_cd=#{param.storeCd}

and  c.store_cd in

#{store,jdbcType=VARCHAR}

 LIMIT ${param.rows} OFFSET ${param.limitStart}

SELECT count(1) from ma1000 e right join (select store_cd, COALESCE(count(store_cd),0) AS total_open_po from od0000 where 1=1
and store_cd=#{param.storeCd}
and store_cd in
#{store,jdbcType=VARCHAR}
and
order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date AND order_sts !='01' GROUP BY store_cd) a on e.store_cd=a.store_cd left join (select store_cd, COALESCE(count(store_cd),0) as no_of_confi_po from od0000 where 1=1
and store_cd=#{param.storeCd}
and store_cd in
#{store,jdbcType=VARCHAR}
and order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date AND review_status='20' GROUP BY store_cd) b on a.store_cd=b.store_cd left join (select store_cd, COALESCE(count(store_cd),0) as no_of_pending_grpo from od0000 where 1=1
and store_cd=#{param.storeCd}
and store_cd in
#{store,jdbcType=VARCHAR}
and order_date > #{param.hhstartDate} AND #{param.hhendDate}>order_date AND review_status='15' GROUP BY store_cd) c on b.store_cd=c.store_cd left join (select store_cd, COALESCE(count(store_cd),0) AS grpo_of_qty from od0000 where 1=1
and store_cd=#{param.storeCd}
and store_cd in
#{store,jdbcType=VARCHAR}
