mapper.select:
SELECT A.acc_id, CONCAT(A.create_ymd,A.create_hms) AS acc_date, ma1000.store_cd, ma1000.store_name, A.customer_name, A.company_name, A.tax, A.address, A.phone, A.email, A.phone2, A.email2, A.receipt_no, A.sale_serial_no, A.amt, B.code_name AS statusName, A.status, A.issue_user_id, A.issue_ymd, A.issue_hms, A.create_user_id, A.create_ymd, A.create_hms, A.pos_id, C.num FROM sa0230 A, CM9010 B, MA1000, ( SELECT COUNT (A.acc_id) AS num FROM sa0230 A, MA1000, CM9010 B WHERE B.code_type = '00540' AND B.code_value = A.status AND ma1000.store_cd = A.store_cd AND A.acc_date BETWEEN ma1000.effective_start_date AND ma1000.effective_end_date AND A.store_cd IN
#{store,jdbcType=VARCHAR}
AND A.acc_date BETWEEN #{param.startDate} AND #{param.endDate}
AND A.status = #{param.status}
AND lower(A.customer_name) like lower(CONCAT('%',#{param.customerName},'%'))
AND lower(A.phone) like lower(CONCAT('%',#{param.phone},'%'))
) C WHERE B.code_type = '00540' AND ma1000.store_cd = A.store_cd AND A.acc_date BETWEEN ma1000.effective_start_date AND ma1000.effective_end_date AND B.code_value = A.status AND A.store_cd IN
#{store,jdbcType=VARCHAR}
AND A.acc_date BETWEEN #{param.startDate} AND #{param.endDate}
AND A.status = #{param.status}
AND A.posId = #{param.posId}
AND lower(A.customer_name) like lower(CONCAT('%',#{param.customerName},'%'))
AND lower(A.phone) like lower(CONCAT('%',#{param.phone},'%'))
ORDER BY acc_date
LIMIT ${param.rows} OFFSET ${param.limitStart}

SELECT A.store_cd, A.tran_serial_no AS receiptNo, A.sale_serial_no , A.tran_date AS date, A.sale_amount AS amt, A.pos_id, B.num FROM SA0010 A, ( SELECT count (S.tran_serial_no) as num FROM SA0010 S WHERE 1=1
AND S.store_cd = #{param.storeNo}
AND S.acc_date BETWEEN #{param.startDate} AND #{param.endDate}
AND cast(sale_serial_no AS VARCHAR) like concat('%',#{param.receiptNo},'%')
AND S.tran_type = 'sale' ) B WHERE 1=1
AND A.store_cd = #{param.storeNo}
AND A.pos_id = #{param.posId}
AND A.acc_date BETWEEN #{param.startDate} AND #{param.endDate}
AND cast(sale_serial_no AS VARCHAR) like concat('%',#{param.receiptNo},'%')
AND A.tran_type = 'sale'
ORDER BY acc_date LIMIT ${param.rows} OFFSET ${param.limitStart}
