mapper.select:

select

  SQL_CALC_FOUND_ROWS


  distinct


from ma1100




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}


select 

from ma1100
where article_id = #{articleId,jdbcType=VARCHAR}
  and effective_start_date = #{effectiveStartDate,jdbcType=CHAR}

mapper.delete:

delete from ma1100
where article_id = #{articleId,jdbcType=VARCHAR}
  and effective_start_date = #{effectiveStartDate,jdbcType=CHAR}

mapper.select:
select a.article_id k,CONCAT(a.article_id, ' ',a.article_name) v from MA1100 a,ma2240 b
where

    a.effective_start_date <= #{businessDate,jdbcType=VARCHAR}
    and a.effective_end_date >= #{businessDate,jdbcType=VARCHAR}

and a.article_id = b.article_id
and b.store_cd = #{storeCd,jdbcType=VARCHAR}

  and (lower(a.article_id) like '%'|| lower(#{articleId,jdbcType=VARCHAR}) || '%'
  or lower(a.article_name) like '%'|| lower(#{articleId,jdbcType=VARCHAR}) || '%')
