mapper.select:

select

  SQL_CALC_FOUND_ROWS


  distinct


from ma1140




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}


select 

from ma1140
where article_id = #{articleId,jdbcType=VARCHAR}
  and barcode = #{barcode,jdbcType=VARCHAR}
  and effective_start_date = #{effectiveStartDate,jdbcType=CHAR}

mapper.delete:

delete from ma1140
where article_id = #{articleId,jdbcType=VARCHAR}
  and barcode = #{barcode,jdbcType=VARCHAR}
  and effective_start_date = #{effectiveStartDate,jdbcType=CHAR}
