mapper.select:

select

  SQL_CALC_FOUND_ROWS


  distinct


from od0000




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}


select

from od0000
where store_cd = #{storeCd,jdbcType=CHAR}
and order_id = #{orderId,jdbcType=VARCHAR}

