mapper.select:
select
distinct
ma8040.article_id k, CONCAT(ma8040.article_id,' ',ma8040.article_name) AS v
from
ma1105,
ma2240,
/*od0010,*/
sk0000_today right join ma8040 on
ma8040.article_id = sk0000_today.article_id
and sk0000_today.effective_date = #{orderDate,jdbcType=VARCHAR}
and sk0000_today.store_cd = #{storeCd,jdbcType=VARCHAR},
(select ma8140.article_id,max(ma8140.barcode) as barcode from ma8140
where ma8140.effective_date = #{orderDate,jdbcType=VARCHAR}
group by ma8140.article_id) c
where
ma8040.article_id = c.article_id
and ma1105.item_code = ma8040.article_id
and ma1105.store_cd = #{storeCd,jdbcType=VARCHAR}
/*and od0010.article_id = ma8040.article_id*/
and ma8040.effective_date = #{orderDate,jdbcType=VARCHAR}

    and ma8040.vendor_id = #{vendorId,jdbcType=VARCHAR}


    and ma8040.article_id = #{articleId,jdbcType=VARCHAR}

/*限制为dc订货*/
and ma8040.dc_item = '1'
/*限制dc订货为非暂停*/
and ma8040.order_pause_dc_flg = '1'
/*取对应供应商商品*/
and ma2240.store_cd = #{storeCd,jdbcType=VARCHAR}
and ma2240.article_id = ma8040.article_id
and ma2240.vendor_id = ma8040.vendor_id
/*限制区域*/
and ma8040.structure_cd = (
select admin_structure_cd from MA0020 where structure_cd =
(SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = #{storeCd,jdbcType=VARCHAR} and effective_sts = '10') and effective_sts = '10'
)

    AND (
    lower(ma8040.article_id) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%'))
    OR lower(ma8040.article_name) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%'))
    )

order by ma8040.article_id ASC

select distinct c.barcode, ma8040.article_id, ma8040.article_name, ma8040.vendor_id, ma8040.purchase_unit_id, ma8040.base_order_price order_price,/*原价*/ ma8040.order_batch_qty, ma8040.min_order_amt_tax,/*最低订货金额*/ ma8040.sale_price as sale_price, ma1100.converter, ma8040.dc_m_order_qty min_order_qty,/*dc最低订货量*/ ma8040.min_display_qty, sk0000_today.advise_qty as auto_order_qty, ma8040.purchase_unit_id as unit_id, ma8040.order_tax_type_cd, (select ma8070.psd ma8070 from ma8070 where ma8070.store_cd=#{storeCd,jdbcType=VARCHAR} and ma8070.article_id=ma8040.article_id) as psd, ma8040.spec, ma0220.unit_name, tax.tax_rate tax_rate /*od0010.order_qty as order_qty, coalesce(od0010.order_qty,0) * coalesce(ma8040.order_price,0) as order_total_amt*/ from ma1100, ma1105, ma2240, /* od0010,*/ sk0000_today right join ma8040 on ma8040.article_id = sk0000_today.article_id and sk0000_today.effective_date = #{orderDate,jdbcType=VARCHAR} and sk0000_today.store_cd = #{storeCd,jdbcType=VARCHAR} left join ma0220 ON ma8040.purchase_unit_id = ma0220.unit_id left join ( select cast(code_name as numeric(4,4)) as tax_rate, code_value,dealer_reserved_1 from cm9010 where code_type='00120' ) tax on tax.code_value = ma8040.order_tax_type_cd , (select ma8140.article_id,max(ma8140.barcode) as barcode from ma8140 where ma8140.effective_date = #{orderDate,jdbcType=VARCHAR} group by ma8140.article_id) c where ma8040.article_id = c.article_id and ma1100.article_id = ma8040.article_id and #{orderDate,jdbcType=VARCHAR} BETWEEN ma1100.effective_start_date AND ma1100.effective_end_date /*限制货架信息*/ and ma1105.item_code = ma8040.article_id and ma1105.store_cd = #{storeCd,jdbcType=VARCHAR} /*and od0010.article_id = ma8040.article_id*/ and ma8040.effective_date = #{orderDate,jdbcType=VARCHAR}
and ma8040.article_id = #{articleId,jdbcType=VARCHAR}
/*限制为dc订货*/ and ma8040.dc_item = '1' /*限制dc订货为非暂停*/ and ma8040.order_pause_dc_flg = '1' /*取对应供应商商品*/ and ma2240.store_cd = #{storeCd,jdbcType=VARCHAR} and ma2240.article_id = ma8040.article_id and ma2240.vendor_id = ma8040.vendor_id /*限制区域*/ and ma8040.structure_cd = ( select admin_structure_cd from MA0020 where structure_cd = (SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = #{storeCd,jdbcType=VARCHAR} and effective_sts = '10') and effective_sts = '10' ) order by ma8040.article_id ASC

