SELECT o.*,o2.order_type_name, o3.order_sts_name, MA2000.vendor_name, o.create_user_id, ma4200.emp_name create_user_name, (select sum(od0010.order_qty)+sum(od0010.order_nocharge_qty) from od0010 where o.order_id=od0010.order_id) order_qty FROM OD0000 O LEFT JOIN ( SELECT code_value order_type_code, code_name AS order_type_name FROM CM9010 WHERE code_type = '00145' ) O2 ON o.order_type = O2.order_type_code LEFT JOIN ( SELECT code_value AS order_sts_code, code_name AS order_sts_name FROM CM9010 WHERE code_type = '00150' ) O3 ON o.order_sts = O3.order_sts_code LEFT JOIN MA2000 on #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date AND o.vendor_id = MA2000.vendor_id LEFT JOIN MA4200 on ma4200.emp_num_id = o.create_user_id where o.order_differentiate = '0' and o.order_type = '01' /*订货*/ and o.store_cd in
#{store,jdbcType=VARCHAR}
and o.order_sts = #{orderSts,jdbcType=CHAR}
and o.review_status = cast (#{reviewStatus,jdbcType=CHAR} as smallint)
and o.order_date >= #{orderStartDate,jdbcType=CHAR}
and o.order_date <= #{orderEndDate,jdbcType=CHAR}
AND lower(o.order_id) like lower(CONCAT('%',#{orderId,jdbcType=VARCHAR},'%'))
AND lower(o.vendor_id) like lower(CONCAT('%',#{vendorId,jdbcType=VARCHAR},'%'))
order by ${orderByClause}
limit ${limitEnd} offset ${limitStart}

SELECT count(o.order_id) FROM OD0000 O where o.order_differentiate = '0' and o.order_type = '01' /*订货*/ and o.store_cd in
#{store,jdbcType=VARCHAR}
and o.order_sts = #{orderSts,jdbcType=CHAR}
and o.review_status = cast (#{reviewStatus,jdbcType=CHAR} as smallint)
and o.order_date >= #{orderStartDate,jdbcType=CHAR}
and o.order_date <= #{orderEndDate,jdbcType=CHAR}
AND lower(o.order_id) like lower(CONCAT('%',#{orderId,jdbcType=VARCHAR},'%'))
AND lower(o.vendor_id) like lower(CONCAT('%',#{vendorId,jdbcType=VARCHAR},'%'))

select distinct c.barcode, ma8040.article_id, ma8040.article_name, ma8040.vendor_id, ma8040.purchase_unit_id, ma8040.base_order_price order_price,/*原价*/ ma8040.order_batch_qty, ma8040.min_order_amt_tax,/*最低订货金额*/ ma8040.sale_price as sale_price, ma1100.converter, /*ma8040.min_order_qty,*/ COALESCE(ma8040.dc_m_order_qty, 0) min_order_qty,/*dc moq*/ ma8040.min_display_qty, sk0000_today.advise_qty as auto_order_qty, ma8040.purchase_unit_id as unit_id, ma8040.order_tax_type_cd,/*税区分*/ tax.tax_rate,/*税率*/ (select ma8070.psd ma8070 from ma8070 where ma8070.store_cd=#{storeCd,jdbcType=VARCHAR} and ma8070.article_id=ma8040.article_id) as psd, ma8040.spec, ma0220.unit_name /*od0010.order_qty as order_qty, coalesce(od0010.order_qty,0) * coalesce(ma8040.order_price,0) as order_total_amt*/ from ma1100, ma1105, ma2240, /*od0010,*/ sk0000_today right join ma8040 on ma8040.article_id = sk0000_today.article_id and sk0000_today.effective_date = #{orderDate,jdbcType=VARCHAR} and sk0000_today.store_cd = #{storeCd,jdbcType=VARCHAR} left join ma0220 ON ma8040.purchase_unit_id = ma0220.unit_id LEFT JOIN ( select cast(code_name as numeric(4,4)) as tax_rate, code_value,dealer_reserved_1 from cm9010 where code_type='00120' ) tax on tax.code_value = ma8040.order_tax_type_cd , (select ma8140.article_id,max(ma8140.barcode) as barcode from ma8140 where ma8140.effective_date = #{orderDate,jdbcType=VARCHAR} group by ma8140.article_id) c where ma8040.article_id = c.article_id and ma1100.article_id = ma8040.article_id and #{orderDate,jdbcType=VARCHAR} BETWEEN ma1100.effective_start_date AND ma1100.effective_end_date /*限制货架信息*/ and ma1105.item_code = ma8040.article_id and ma1105.store_cd = #{storeCd,jdbcType=VARCHAR} /*and od0010.article_id = ma8040.article_id*/ and ma8040.effective_date = #{orderDate,jdbcType=VARCHAR}
and ma8040.article_id = #{articleId,jdbcType=VARCHAR}
/*限制为dc订货*/ and ma8040.dc_item = '1' /*限制dc订货为非暂停*/ and ma8040.order_pause_dc_flg = '1' /*取对应供应商商品*/ and ma2240.store_cd = #{storeCd,jdbcType=VARCHAR} and ma2240.article_id = ma8040.article_id and ma2240.vendor_id = ma8040.vendor_id /*限制区域*/ and ma8040.structure_cd = ( select admin_structure_cd from MA0020 where structure_cd = (SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = #{storeCd,jdbcType=VARCHAR} and effective_sts = '10') and effective_sts = '10' ) limit 1

SELECT DISTINCT od0000.order_id, od0000.store_cd, od0000.vendor_id, ( SELECT COUNT ( * ) FROM od0000 T WHERE T.store_cd = od0000.store_cd AND T.vendor_id = od0000.vendor_id
AND T.update_ymd < #{auditDate,jdbcType=VARCHAR} AND T.order_type = '01' AND T.review_status >= 10
) order_qty FROM od0000, ma1000, cm9060 WHERE od0000.order_id = #{orderId,jdbcType=VARCHAR} AND od0000.store_cd = ma1000.store_cd AND #{auditDate,jdbcType=VARCHAR} BETWEEN ma1000.effective_start_date AND ma1000.effective_end_date AND cm9060.sp_id = '0221'
AND #{auditDate,jdbcType=VARCHAR} <= to_char( to_date( ma1000.open_date, 'yyyymmdd' ) + CAST ( cm9060.sp_value AS INT ), 'yyyymmdd' );

SELECT CAST(ma2170.this_store_1st_support AS NUMERIC ( 19, 6 )) / 100 AS discount_rate FROM ma2100, ma2170 WHERE ma2100.vendor_id = #{vendorId,jdbcType=VARCHAR} AND ma2100.trading_term_no = ma2170.trading_term_no AND ma2100.effective_start_date = ma2170.effective_start_date
AND ma2100.this_contract_start_date <= #{auditDate,jdbcType=VARCHAR} AND to_char( to_timestamp( ma2100.this_contract_end_date, 'yyyyMMdd' ) + ( CASE WHEN ma2100.this_auto_extend = '' OR ma2100.this_auto_extend IS NULL THEN '0' ELSE ma2100.this_auto_extend END || ' month' ) :: INTERVAL, 'yyyyMMdd' ) >= #{auditDate,jdbcType=VARCHAR}
LIMIT 1

mapper.update:
/*更新商品详细*/
UPDATE od0010
    SET
    /*设置未税折扣金额*/
    discount_amt_notax = COALESCE(order_amt_notax,0) * (1 - #{discountRate,jdbcType=NUMERIC}),
    /*设置含税税折扣金额*/
    discount_amt = COALESCE(order_amt,0) * (1 - #{discountRate,jdbcType=NUMERIC}),
    /*设置折扣税额*/
    discount_tax = COALESCE(order_tax,0) * (1 - #{discountRate,jdbcType=NUMERIC})
WHERE order_id = #{orderId,jdbcType=VARCHAR};
/*更新头档*/
UPDATE od0000
    SET
    /*设置折扣率*/
    discount_rate = #{discountRate,jdbcType=NUMERIC},
    /*设置含税税折扣金额*/
    discount_amt = COALESCE(order_amt,0) * (1 - #{discountRate,jdbcType=NUMERIC}),
    /*设置折扣税额*/
    discount_tax = COALESCE(order_tax,0) * (1 - #{discountRate,jdbcType=NUMERIC}),
    /*设置未税折扣金额*/
    discount_amt_notax = (COALESCE(order_amt,0) * (1 - #{discountRate,jdbcType=NUMERIC})) - (COALESCE(order_tax,0) * (1 - #{discountRate,jdbcType=NUMERIC}))
WHERE order_id = #{orderId,jdbcType=VARCHAR};

mapper.select:
