mapper.select:
SELECT T.*, T1.code_name AS method_name, T2.code_name AS order_error_name, T3.unit_name AS order_uom FROM ( SELECT * FROM OD8040
)T LEFT JOIN ( SELECT code_type, code_value, code_name FROM CM9010 WHERE code_type = '00065' ) T1 ON T.order_method = T1.code_value LEFT JOIN ( SELECT code_type, code_value, code_name FROM CM9010 WHERE code_type = '00075' ) T2 ON T.order_error_cd = T2.code_value LEFT JOIN ( SELECT unit_id, unit_name FROM MA0220 ) T3 ON T.receive_unit_id = T3.unit_id ORDER BY order_date DESC
LIMIT ${rows} OFFSET ${limitStart}
