SELECT T.*,T.order_date,T5.warehouse_name,T3.store_name FROM ( SELECT distinct store_cd,order_id,order_date,A.review_status,vendor_id,delivery_date,A.order_method FROM OD0000 A WHERE A.order_date =#{param.orderDate} AND A.order_type='01'
AND A.store_cd=#{param.storeCd }
) AS T LEFT JOIN ( SELECT vendor_id, vendor_name FROM MA2000 ) T2 ON T.vendor_id = T2.vendor_id LEFT JOIN ( SELECT store_cd, store_name, zo_cd FROM MA1000) T3 on T.store_cd=T3.store_cd left join( Select * from ma5321 ) T5 on T.vendor_id=T5.warehouse_no

SELECT B.*, D.* FROM ( SELECT DISTINCT COALESCE(PI1.variance_qty,0) variance_qty, OD0010.order_id, sk0000.on_hand_qty qty1, od0010.barcode, od0010.article_id, ma1100.article_name, od0010.vendor_id, od0010.order_unit purchase_unit_id, od0010.order_price, ma1100.converter, ma1100.spec, COALESCE ( receive_qty, 0 ) AS receive_qty, ma1110.order_batch_qty, ma1110.min_order_amt_tax, /*最低订货金额*/ COALESCE ( min_order_qty, 0 ) AS min_order_qty, COALESCE ( dc_m_order_qty, 0 ) AS dc_m_order_qty, ma1110.order_tax_type_cd, /*税率cd*/ ma1100.min_display_qty, sk0000.advise_qty AS auto_order_qty, od0010.order_unit AS unit_id, ma0220.unit_name, od0010.order_qty AS order_qty, od0010.order_nocharge_qty, od0010.order_qty + COALESCE ( od0010.order_nocharge_qty, 0 ) AS real_qty, ( SELECT ma8070.psd ma8070 FROM ma8070 WHERE ma8070.store_cd = #{param.storeCd} AND ma8070.article_id = od0010.article_id ) AS psd, od0010.order_amt AS order_total_amt FROM ma1000, ma1100, ma1110, PI0125 PI1 LEFT JOIN PI0120 PI2 ON pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.article_id = pi2.article_id, od0010 LEFT JOIN sk0000 ON od0010.article_id = sk0000.article_id AND sk0000.store_cd = #{param.storeCd} AND sk0000.store_cd = od0010.store_cd AND sk0000.effective_date = #{param.orderDate} LEFT JOIN ma0220 ON od0010.order_unit = ma0220.unit_id WHERE ma1100.article_id = od0010.article_id AND pi1.store_cd =#{param.storeCd} AND pi1.article_id = ma1100.article_id AND ma1000.store_cd = od0010.store_cd AND #{param.orderDate} BETWEEN ma1000.effective_start_date AND ma1000.effective_end_date AND #{param.orderDate} BETWEEN ma1100.effective_start_date AND ma1100.effective_end_date AND ma1100.article_id = ma1110.article_id
AND lower(ma1110.default_barcode) like lower(CONCAT('%',#{param.articleId,jdbcType=VARCHAR},'%'))
AND ma1100.dep_cd = #{param.depCd,jdbcType=VARCHAR}
AND ma1100.pma_cd = #{param.pmaCd,jdbcType=VARCHAR}
AND ma1100.category_cd = #{param.categoryCd,jdbcType=VARCHAR}
AND ma1100.sub_category_cd = #{param.subCategoryCd,jdbcType=VARCHAR}
AND lower( ma1100.article_name) like lower(CONCAT('%',#{param.articleName,jdbcType=VARCHAR},'%'))
AND ma1000.ofc = #{param.am}
AND ma1110.structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = od0010.store_cd AND effective_sts = '10' ) AND effective_sts = '10' ) AND od0010.vendor_id = ma1110.vendor_id ) D LEFT JOIN ( SELECT T .*, T5.warehouse_name, T3.store_name FROM ( SELECT DISTINCT store_cd, order_id, order_date, A.review_status, vendor_id, delivery_date, A.order_method FROM OD0000 A WHERE A.store_cd IN
#{store,jdbcType=VARCHAR}
AND A.order_date=#{param.orderDate} AND A.store_cd=#{param.storeCd}
) AS T LEFT JOIN ( SELECT vendor_id, vendor_name FROM MA2000 ) T2 ON T.vendor_id = T2.vendor_id LEFT JOIN ( SELECT store_cd, store_name, zo_cd FROM MA1000 ) T3 ON T.store_cd = T3.store_cd LEFT JOIN ( SELECT * FROM ma5321 ) T5 ON T.vendor_id = T5.warehouse_no ) B ON D.order_id = B.order_id WHERE store_cd=#{param.storeCd} LIMIT ${param.rows} OFFSET ${param.limitStart}

SELECT DISTINCT ma1000.store_cd, ma1000.store_name, COALESCE(PI1.variance_qty,0) variance_qty, OD0010.order_id, sk0000.on_hand_qty qty1, od0010.barcode, od0010.article_id, ma1100.article_name, od0010.vendor_id, od0010.order_unit purchase_unit_id, od0010.order_price, ma1100.converter, ma1100.spec, COALESCE ( receive_qty, 0 ) AS receive_qty, ma1110.order_batch_qty, ma1110.min_order_amt_tax, /*最低订货金额*/ COALESCE ( min_order_qty, 0 ) AS min_order_qty, COALESCE ( dc_m_order_qty, 0 ) AS dc_m_order_qty, ma1110.order_tax_type_cd, /*税率cd*/ ma1100.min_display_qty, coalesce (sk0000.advise_qty,0) AS auto_order_qty, od0010.order_unit AS unit_id, ma0220.unit_name, od0010.order_qty AS order_qty, od0010.order_nocharge_qty, od0010.order_qty + COALESCE ( od0010.order_nocharge_qty, 0 ) AS real_qty, COALESCE (( SELECT ma8070.psd FROM ma8070 WHERE ma8070.store_cd = #{param.storeCd} AND od0010.article_id=ma8070.article_id) ,0) AS psd, od0010.order_amt AS order_total_amt FROM ma1000, ma1100, ma1110, PI0125 PI1 LEFT JOIN PI0120 PI2 ON pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.article_id = pi2.article_id, od0010 LEFT JOIN sk0000 ON od0010.article_id = sk0000.article_id AND sk0000.store_cd = #{param.storeCd} AND sk0000.store_cd = od0010.store_cd AND sk0000.effective_date = #{param.orderDate} LEFT JOIN ma0220 ON od0010.order_unit = ma0220.unit_id WHERE ma1100.article_id = od0010.article_id AND pi1.store_cd =#{param.storeCd} AND pi1.article_id = ma1100.article_id AND ma1000.store_cd = od0010.store_cd AND #{param.orderDate} BETWEEN ma1000.effective_start_date AND ma1000.effective_end_date AND #{param.orderDate} BETWEEN ma1100.effective_start_date AND ma1100.effective_end_date AND ma1100.article_id = ma1110.article_id
AND concat(lower( ma1100.article_id ),'',lower( ma1100.article_name ),'',lower( ma1100.article_name_en ),'',lower( ma1100.article_short_name_en )) LIKE lower( CONCAT('%',#{param.articleName},'%') )
AND ma1100.dep_cd = #{param.depCd,jdbcType=VARCHAR}
AND ma1100.pma_cd = #{param.pmaCd,jdbcType=VARCHAR}
AND ma1100.category_cd = #{param.categoryCd,jdbcType=VARCHAR}
AND ma1100.sub_category_cd = #{param.subCategoryCd,jdbcType=VARCHAR}
AND ma1000.ofc = #{param.am}
AND ma1110.structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = od0010.store_cd AND effective_sts = '10' ) AND effective_sts = '10' ) AND od0010.vendor_id = ma1110.vendor_id AND od0010.order_id =#{param.orderId}

