AND lower(ma1110.default_barcode) like lower(CONCAT('%',#{param.articleId,jdbcType=VARCHAR},'%'))
AND ma1100.dep_cd = #{param.depCd,jdbcType=VARCHAR}
AND ma1100.pma_cd = #{param.pmaCd,jdbcType=VARCHAR}
AND ma1100.category_cd = #{param.categoryCd,jdbcType=VARCHAR}
AND ma1100.sub_category_cd = #{param.subCategoryCd,jdbcType=VARCHAR}
AND lower( ma1100.article_name) like lower(CONCAT('%',#{param.articleName,jdbcType=VARCHAR},'%'))
AND ma1000.ofc = #{param.am}
AND ma1110.structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = ( SELECT admin_structure_cd FROM MA0020 WHERE structure_cd = od0010.store_cd AND effective_sts = '10' ) AND effective_sts = '10' ) AND od0010.vendor_id = ma1110.vendor_id ) D LEFT JOIN ( SELECT T .*, T5.warehouse_name, T3.store_name FROM ( SELECT DISTINCT store_cd, order_id, order_date, A.review_status, vendor_id, delivery_date, A.order_method FROM OD0000 A WHERE A.store_cd IN
#{store,jdbcType=VARCHAR}
AND A.order_date=#{param.orderDate} AND A.store_cd=#{param.storeCd}
) AS T LEFT JOIN ( SELECT vendor_id, vendor_name FROM MA2000 ) T2 ON T.vendor_id = T2.vendor_id LEFT JOIN ( SELECT store_cd, store_name, zo_cd FROM MA1000 ) T3 ON T.store_cd = T3.store_cd LEFT JOIN ( SELECT * FROM ma5321 ) T5 ON T.vendor_id = T5.warehouse_no ) B ON D.order_id = B.order_id WHERE store_cd=#{param.storeCd}
