mapper.select:

select

  SQL_CALC_FOUND_ROWS


  distinct


from pi0000




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}


select 

from pi0000
where acc_date = #{accDate,jdbcType=VARCHAR}
  and store_cd = #{storeCd,jdbcType=VARCHAR}
  and dep_cd = #{depCd,jdbcType=CHAR}
  and pma_cd = #{pmaCd,jdbcType=CHAR}
  and category_cd = #{categoryCd,jdbcType=CHAR}
  and sub_category_cd = #{subCategoryCd,jdbcType=CHAR}

mapper.delete:

delete from pi0000
where acc_date = #{accDate,jdbcType=VARCHAR}
  and store_cd = #{storeCd,jdbcType=VARCHAR}
  and dep_cd = #{depCd,jdbcType=CHAR}
  and pma_cd = #{pmaCd,jdbcType=CHAR}
  and category_cd = #{categoryCd,jdbcType=CHAR}
  and sub_category_cd = #{subCategoryCd,jdbcType=CHAR}
