mapper.select:
SELECT count (a.*) FROM ( SELECT DISTINCT ma1.store_cd AS storeCd, ma3.store_name AS storeName, ma1.article_id AS articleId, ma1.barcode, ma1.article_name AS articleName, ma1.old_price AS oldPrice, ma1.new_price AS newPrice, ma1.name_en_price_label AS priceLabelEnName, ma1.name_vn_price_label AS priceLabelVnName, ma1.effective_start_date AS effectiveStartDate, ma07.dep_cd, concat ( ma07.dep_cd, ' ', ma07.dep_name ) dep_name, ma08.pma_cd, concat ( ma08.pma_cd, ' ', ma08.pma_name ) pma_name, ma09.category_cd, concat ( ma09.category_cd, ' ', ma09.category_name ) category_name, ma01.sub_category_cd, concat ( ma01.sub_category_cd, ' ', ma01.sub_category_name ) sub_category_name FROM ma8300 ma1, ma1000 ma3, ma0070 ma07, ma0080 ma08, ma0090 ma09, ma0100 ma01 WHERE ma3.store_cd = ma1.store_cd AND ma1.pma_cd = ma08.pma_cd AND ma1.dep_cd = ma07.dep_cd AND ma1.category_cd = ma09.category_cd AND ma1.sub_category_cd = ma01.sub_category_cd
AND ma1.dep_cd = #{param.depCd}
AND ma1.pma_cd = #{param.pmaCd}
and ma1.category_cd = #{param.categoryCd,jdbcType=VARCHAR}
and ma1.sub_category_cd = #{param.subCategoryCd,jdbcType=VARCHAR}
) a

SELECT DISTINCT ma1.store_cd AS storeCd, ma3.store_name AS storeName, ma1.article_id AS articleId, ma1.barcode, ma1.article_name AS articleName, ma1.old_price AS oldPrice, ma1.new_price AS newPrice, ma1.name_en_price_label AS priceLabelEnName, ma1.name_vn_price_label AS priceLabelVnName, ma1.effective_start_date AS effectiveStartDate, ma07.dep_cd, concat ( ma07.dep_cd, ' ', ma07.dep_name ) dep_name, ma08.pma_cd, concat ( ma08.pma_cd, ' ', ma08.pma_name ) pma_name, ma09.category_cd, concat ( ma09.category_cd, ' ', ma09.category_name ) category_name, ma01.sub_category_cd, concat ( ma01.sub_category_cd, ' ', ma01.sub_category_name ) sub_category_name FROM ma8300 ma1, ma1000 ma3, ma0070 ma07, ma0080 ma08, ma0090 ma09, ma0100 ma01 WHERE ma3.store_cd = ma1.store_cd AND ma1.pma_cd = ma08.pma_cd AND ma1.dep_cd = ma07.dep_cd AND ma1.category_cd = ma09.category_cd AND ma1.sub_category_cd = ma01.sub_category_cd
AND ma1.dep_cd = #{param.depCd}
AND ma1.pma_cd = #{param.pmaCd}
and ma1.category_cd = #{param.categoryCd,jdbcType=VARCHAR}
and ma1.sub_category_cd = #{param.subCategoryCd,jdbcType=VARCHAR}
LIMIT ${param.rows} OFFSET ${param.limitStart}
