sql.where:
AND store_cd IN

    #{store,jdbcType=VARCHAR}



        AND order_type = #{orderType,jdbcType=VARCHAR}


        AND order_type IN ('07','08')



    AND order_date >= #{modifyStartDate,jdbcType=VARCHAR}


    AND #{modifyEndDate,jdbcType=VARCHAR} >= order_date


    AND LOWER(order_id) LIKE LOWER(CONCAT('%',#{orderId,jdbcType=VARCHAR},'%'))


    AND LOWER(org_order_id) LIKE LOWER(CONCAT('%',#{orgOrderId,jdbcType=VARCHAR},'%'))


    AND LOWER(vendor_id) LIKE LOWER(CONCAT('%',#{vendorId,jdbcType=VARCHAR},'%'))


    AND review_status = cast(#{reviewStatus,jdbcType=VARCHAR} as NUMERIC)

mapper.select:
SELECT COUNT(1) FROM (SELECT T.*, coalesce (T1.vendor_name, T3.vendor_name) AS vendor_name, T2.store_name, T5.om_name as om, T5.ofc_name as am, T5.oc_name as oc FROM ( SELECT store_cd, order_id, org_order_id, vendor_id, delivery_type_cd, delivery_center_id, delivery_area_cd, shipment, order_type, order_method, payment_type, promotion_id, order_date, delivery_date, expire_date, receive_date, vendor_address, store_address, order_sts, nr_flg, order_remark, purchase_vat_cd, tax_rate, order_amt, order_tax, receive_amt, receive_tax, order_differentiate, upload_flg, upload_date,review_status FROM OD0000
) T LEFT JOIN ( SELECT vendor_id, vendor_name FROM MA2000 WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T1 ON T.vendor_id = T1.vendor_id left JOIN( select warehouse_name AS vendor_name,warehouse_no from ma5321 ) T3 ON T.vendor_id= T3.warehouse_no LEFT JOIN ( SELECT store_cd, store_name FROM MA1000 WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T2 ON T.store_cd = T2.store_cd LEFT JOIN ( SELECT store_cd, store_name,oc,om,ofc,ofcName.c_operatorname as ofc_name,ocName.c_operatorname as oc_name,omName.c_operatorname om_name FROM MA1000 LEFT JOIN (SELECT distinct t_operator.c_operatorcode,t_operator.c_operatorname FROM t_operator LEFT JOIN MA1000 ON MA1000.ofc= t_operator.c_operatorcode where 1=1
) ofcName ON ma1000.ofc=ofcName.c_operatorcode LEFT JOIN (SELECT distinct t_operator.c_operatorcode,t_operator.c_operatorname FROM t_operator LEFT JOIN MA1000 ON MA1000.oc= t_operator.c_operatorcode where 1=1 ) AS ocName ON ma1000.oc=ocName.c_operatorcode LEFT JOIN (SELECT distinct t_operator.c_operatorcode,t_operator.c_operatorname FROM t_operator LEFT JOIN MA1000 ON MA1000.om= t_operator.c_operatorcode where 1=1
) AS omName ON ma1000.om=omName.c_operatorcode WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T5 ON t5.store_cd=T2.store_cd where 1=1
And T5.ofc=#{am,jdbcType=VARCHAR}
And T5.oc=#{oc,jdbcType=VARCHAR}
And T5.om =#{om,jdbcType=VARCHAR}
ORDER BY T.receive_date, T.vendor_id, T.order_id ASC) B

SELECT T.*, coalesce (T1.vendor_name, T3.vendor_name) AS vendor_name, T2.store_name, T5.om_name as om, T5.ofc_name as am, T5.oc_name as oc FROM ( SELECT store_cd, order_id, org_order_id, vendor_id, delivery_type_cd, delivery_center_id, delivery_area_cd, shipment, order_type, order_method, payment_type, promotion_id, order_date, delivery_date, expire_date, receive_date, vendor_address, store_address, order_sts, nr_flg, order_remark, purchase_vat_cd, tax_rate, order_amt, order_tax, receive_amt, receive_tax, order_differentiate, upload_flg, upload_date,review_status FROM OD0000
) T LEFT JOIN ( SELECT vendor_id, vendor_name FROM MA2000 WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T1 ON T.vendor_id = T1.vendor_id left JOIN( select warehouse_name AS vendor_name,warehouse_no from ma5321 ) T3 ON T.vendor_id= T3.warehouse_no LEFT JOIN ( SELECT store_cd, store_name FROM MA1000 WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T2 ON T.store_cd = T2.store_cd LEFT JOIN ( SELECT store_cd, store_name,oc,om,ofc,ofcName.c_operatorname as ofc_name,ocName.c_operatorname as oc_name,omName.c_operatorname om_name FROM MA1000 LEFT JOIN (SELECT distinct t_operator.c_operatorcode,t_operator.c_operatorname FROM t_operator LEFT JOIN MA1000 ON MA1000.ofc= t_operator.c_operatorcode where 1=1
) ofcName ON ma1000.ofc=ofcName.c_operatorcode LEFT JOIN (SELECT distinct t_operator.c_operatorcode,t_operator.c_operatorname FROM t_operator LEFT JOIN MA1000 ON MA1000.oc= t_operator.c_operatorcode where 1=1 ) AS ocName ON ma1000.oc=ocName.c_operatorcode LEFT JOIN (SELECT distinct t_operator.c_operatorcode,t_operator.c_operatorname FROM t_operator LEFT JOIN MA1000 ON MA1000.om= t_operator.c_operatorcode where 1=1
) AS omName ON ma1000.om=omName.c_operatorcode WHERE #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date ) T5 ON t5.store_cd=T2.store_cd where 1=1
And T5.ofc=#{am,jdbcType=VARCHAR}
And T5.oc=#{oc,jdbcType=VARCHAR}
And T5.om =#{om,jdbcType=VARCHAR}
ORDER BY T.receive_date, T.vendor_id, T.order_id ASC
LIMIT ${rows} OFFSET ${limitStart}

        SELECT
