mapper.select:
select distinct ma1100.article_id k, CONCAT(ma1100.article_id,' ',ma1100.article_name) AS v from od0010,ma1100 ,sk0000_today
and od0010.article_id = ma1100.article_id and sk0000_today.article_id = od0010.article_id and sk0000_today.store_cd = od0010.store_cd /*判断为已收货的商品*/ and od0010.receive_total_qty <> 0
/*搭赠商品不能退货*/ and od0010.is_free_item = '0' and #{orderDate,jdbcType=VARCHAR} BETWEEN ma1100.effective_start_date AND ma1100.effective_end_date
and od0010.order_id = #{orderId,jdbcType=VARCHAR}
AND ( lower(ma1100.article_id) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) OR lower(ma1100.article_name) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) )
order by ma1100.article_id ASC

select distinct od0010.barcode, od0010.article_id, od0010.store_cd, /*receive_total_qty为收货数量*/ coalesce(od0010.receive_total_qty,0) receive_qty, coalesce(od0010.order_qty,0) order_qty, coalesce(od0010.order_nocharge_qty,0) order_nocharge_qty, coalesce(od0010.receive_nocharge_qty,0) receive_nocharge_qty, od0010.vendor_id, ma1100.article_name, ma1100.sales_unit_id as unit_id, ma1100.spec, ma1100.article_type, ma0220.unit_name, sk0000_today.order_price, sk0000_today.order_tax_type_cd,/*税区分*/ sk0000_today.order_tax_rate, /*税率*/ (select COALESCE(sum(t1.receive_qty),0) from od0000 t,od0010 t1
t.order_id = t1.order_id
and t.org_order_id = #{orderId,jdbcType=VARCHAR}
and t1.article_id = #{articleId,jdbcType=VARCHAR} and t.order_type = '02' and t.order_sts = '04'
) returned_qty, /*已退货数量*/ od0010.is_free_item from od0010, sk0000_today, ma1100 left join ma0220 ON ma1100.sales_unit_id = ma0220.unit_id where ma1100.article_id = od0010.article_id and sk0000_today.effective_date = #{orderDate,jdbcType=VARCHAR} and sk0000_today.article_id = od0010.article_id and sk0000_today.store_cd = od0010.store_cd and #{orderDate,jdbcType=VARCHAR} BETWEEN ma1100.effective_start_date AND ma1100.effective_end_date
and od0010.order_id = #{orderId,jdbcType=VARCHAR}
and ma1100.article_id = #{articleId,jdbcType=VARCHAR}
limit 1

select distinct a.order_date, a.order_id, a.org_order_id, a.delivery_center_id as delivery_center_id, d.warehouse_name as delivery_center_name, a.store_cd, e.store_name, a.order_sts, a.return_type, a.create_ymd,a.create_hms, a.review_status review_status, f.review_status status, (select sum(od0010.order_qty)+sum(od0010.order_nocharge_qty) from od0010 where a.order_id=od0010.order_id) order_qty, (select sum(od0010.receive_qty) from od0010 where a.order_id=od0010.order_id) receive_qty from od0000 a left join od0010 b on b.order_id = a.order_id left join ma5321 d on d.warehouse_no = a.delivery_center_id left join ma1000 e on e.store_cd = a.store_cd and a.order_date BETWEEN e.effective_start_date AND e.effective_end_date left join od0005 f on a.order_id = f.order_id where a.order_type = '02' and a.order_differentiate = '1' /*dc退货*/ and a.store_cd IN
#{store,jdbcType=VARCHAR}
and A.order_date>=#{orderStartDate}
and #{orderEndDate}>= A.order_date
AND lower(A.order_id) like lower(CONCAT('%',#{orderId},'%'))
AND lower(A.org_order_id) like lower(CONCAT('%',#{orgOrderId},'%'))
AND A.vendor_id = #{deliveryCenterId}
and A.review_status = #{reviewStatus,jdbcType=INTEGER}
and f.review_status = #{status,jdbcType=INTEGER}
and A.return_type = #{returnType}
and A.review_status in (10,20)
order by a.create_ymd desc,a.create_hms desc
limit ${limitEnd} offset ${limitStart}

select distinct a.order_date, a.order_id, a.org_order_id, a.delivery_center_id as delivery_center_id, d.warehouse_name as delivery_center_name, a.store_cd, e.store_name, a.order_sts, a.return_type, a.create_ymd,a.create_hms, a.review_status review_status, f.review_status status, (select sum(od0010.order_qty)+sum(od0010.order_nocharge_qty) from od0010 where a.order_id=od0010.order_id) order_qty, (select sum(od0010.receive_qty) from od0010 where a.order_id=od0010.order_id) receive_qty from od0000 a left join od0010 b on b.order_id = a.order_id left join ma5321 d on d.warehouse_no = a.delivery_center_id left join ma1000 e on e.store_cd = a.store_cd and a.order_date BETWEEN e.effective_start_date AND e.effective_end_date left join od0005 f on a.order_id = f.order_id where a.order_type = '02' and a.order_differentiate = '1' /*dc退货*/ and a.store_cd IN
#{store,jdbcType=VARCHAR}
and A.order_date>=#{orderStartDate}
and #{orderEndDate}>= A.order_date
AND lower(A.order_id) like lower(CONCAT('%',#{orderId},'%'))
AND lower(A.org_order_id) like lower(CONCAT('%',#{orgOrderId},'%'))
AND A.vendor_id = #{deliveryCenterId}
and A.review_status = #{reviewStatus,jdbcType=INTEGER}
and f.review_status = #{status,jdbcType=INTEGER}
and A.return_type = #{returnType}
and A.review_status in (10,20)
order by a.create_ymd desc,a.create_hms desc

SELECT COUNT (temp_table.order_id) from (select distinct a.order_date, a.order_id, a.org_order_id, a.delivery_center_id as delivery_center_id, d.warehouse_name as delivery_center_name, a.store_cd, e.store_name, a.order_sts, a.return_type, a.create_ymd,a.create_hms, a.review_status review_status, f.review_status status, (select sum(od0010.order_qty)+sum(od0010.order_nocharge_qty) from od0010 where a.order_id=od0010.order_id) order_qty, (select sum(od0010.receive_qty) from od0010 where a.order_id=od0010.order_id) receive_qty from od0000 a left join od0010 b on b.order_id = a.order_id left join ma5321 d on d.warehouse_no = a.delivery_center_id left join ma1000 e on e.store_cd = a.store_cd and a.order_date BETWEEN e.effective_start_date AND e.effective_end_date left join od0005 f on a.order_id = f.order_id where a.order_type = '02' and a.order_differentiate = '1' /*dc退货*/ and a.store_cd IN
#{store,jdbcType=VARCHAR}
and A.order_date>=#{orderStartDate}
and #{orderEndDate}>= A.order_date
AND lower(A.order_id) like lower(CONCAT('%',#{orderId},'%'))
AND lower(A.org_order_id) like lower(CONCAT('%',#{orgOrderId},'%'))
AND A.vendor_id = #{deliveryCenterId}
and A.review_status = #{reviewStatus,jdbcType=INTEGER}
and f.review_status = #{status,jdbcType=INTEGER}
and A.return_type = #{returnType}
and A.review_status in (10,20)
order by a.create_ymd desc,a.create_hms desc
limit ${limitEnd} offset ${limitStart} )AS temp_table

SELECT ma1100.article_id, d.barcode, ma1100.article_name, ma1100.spec, ma1100.sales_unit_id, COALESCE (iv.on_hand_qty,0) on_hand_qty,--昨日库存 COALESCE (iv.on_order_qty,0) on_order_qty,--在途量 COALESCE (iv.sale_qty,0) sale_qty,--销售数量 COALESCE (iv.scrap_qty,0) scrap_qty,--报废数量 COALESCE (iv.store_return_qty,0)+COALESCE (iv.return_corr_qty,0) store_return_qty,--店铺退货 = 店铺退货+退货更正 COALESCE (iv.receive_qty,0)+COALESCE (iv.rec_corr_qty,0) receive_qty,--收货数量 = 收货数量 + 收货数量更正数量 COALESCE (iv.return_corr_qty,0) return_corr_qty,--店铺退货更正数量 COALESCE (iv.rec_corr_qty,0) rec_corr_qty,--收货数量更正数量 COALESCE (iv.adjust_qty,0) adjust_qty,--库存调整数量 COALESCE (iv.transfer_in_qty,0) transfer_in_qty,--调拨增加 COALESCE (iv.transfer_out_qty,0) transfer_out_qty,--调拨减少 COALESCE (iv.real_time_qty, 0 ) AS realtime_qty --实时库存 FROM ma1100 INNER JOIN pipelinedb.inventory_view iv on iv.article_id = ma1100.article_id and iv.store_cd = #{storeCd,jdbcType=VARCHAR} INNER JOIN ( SELECT ma1110.article_id, ma1110.default_barcode barcode, ma1110.vendor_id FROM ma1110 WHERE #{stockDate} BETWEEN ma1110.effective_start_date AND ma1110.effective_end_date ) AS d ON iv.article_id = d.article_id AND iv.major_vendor_id = d.vendor_id
iv.store_cd = #{storeCd,jdbcType=VARCHAR} and #{stockDate} between ma1100.effective_start_date and ma1100.effective_end_date and ma1100.article_id = #{articleId,jdbcType=VARCHAR}
order by ma1100.article_id ASC limit 1
