mapper.select:
select DISTINCT SA0010.tran_type v, SA0010.tran_type k from SA0010 where 1=1
and SA0010.store_cd=#{storeCd}
AND SA0010.pos_id=#{posId}
AND SA0010.acc_date>=#{startDate}
AND #{endDate} >= SA0010.acc_date
AND ( lower(SA0010.tran_type) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) OR lower(SA0010.tran_type) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) )

select distinct b.tran_serial_no, cbs.billno,cbs.transid, a.receipt_content from sa0060 a, sa0010 b left join sa0710 cbs on b.store_cd = cbs.store_cd AND b.pos_id = cbs.pos_id AND b.tran_serial_no = CAST ( cbs.tran_serial_no AS INTEGER ) where a.pos_id = b.pos_id and a.acc_date = b.acc_date and a.pos_id = b.pos_id and a.tran_serial_no = b.tran_serial_no
AND cbs.transid like concat('%',#{ dto.billSaleNo },'%')
AND cbs.billno like concat('%',#{ dto.billSaleNo },'%')
and a.pos_id=#{dto.posNo}
and a.store_cd=#{dto.storeCd}
and b.tran_type=#{dto.tranType}
and a.tran_date >= TO_TIMESTAMP(#{dto.saleDate,jdbcType=VARCHAR}|| ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS') and (a.tran_date - INTERVAL'1 day') < TO_TIMESTAMP(#{dto.saleDate,jdbcType=VARCHAR} || ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS')
and cast(a.sale_serial_no as VARCHAR)=#{dto.saleNo}
and a.tran_serial_no=#{dto.tranSerialNo}
and a.receipt_content like CONCAT('%',#{dto.articleId},'%')
and a.store_cd IN
#{store,jdbcType=VARCHAR}

select distinct SA0020.article_id k, MA1100.article_name v from SA0020, ma1100,sa0010 where SA0020.article_id=MA1100.article_id AND SA0020.pma_cd=MA1100.pma_cd AND SA0020.category_cd=MA1100.category_cd and sa0020.acc_date = sa0010.acc_date and sa0020.store_cd = sa0010.store_cd and sa0020.pos_id = sa0010.pos_id and sa0020.tran_serial_no = sa0010.tran_serial_no and sa0020.sale_serial_no = sa0010.sale_serial_no
AND sa0020.pos_id=#{posId}
and sa0010.tran_date >= TO_TIMESTAMP(#{salesDate,jdbcType=VARCHAR}|| ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS') and (sa0010.tran_date - INTERVAL'1 day') < TO_TIMESTAMP(#{salesDate,jdbcType=VARCHAR} || ' ' ||'06:00:00', 'YYYY-MM-DD HH24:MI:SS')
and sa0010.store_cd=#{storeCd}
AND ( lower( MA1100.article_id) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) OR lower( MA1100.article_name) like lower(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) )
