mapper.select:

select

  SQL_CALC_FOUND_ROWS


  distinct


from sa0160




  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}


select 

from sa0160
where store_cd = #{storeCd,jdbcType=VARCHAR}
  and pos_id = #{posId,jdbcType=VARCHAR}

mapper.delete:

delete from sa0160
where store_cd = #{storeCd,jdbcType=VARCHAR}
  and pos_id = #{posId,jdbcType=VARCHAR}

mapper.select:
SELECT T.store_cd k, CONCAT(T.store_cd,' ',T1.store_name) AS v FROM ( SELECT DISTINCT store_cd FROM sa0160 ) T JOIN( SELECT store_cd, store_name FROM ma1000 WHERE (SELECT sp_value FROM CM9060 WHERE sp_id = '0000') BETWEEN effective_start_date AND effective_end_date
AND (LOWER(store_cd) LIKE LOWER(CONCAT('%',#{v,jdbcType=VARCHAR},'%')) OR LOWER(store_name) LIKE LOWER(CONCAT('%',#{v,jdbcType=VARCHAR},'%')))
) T1 ON T.store_cd = T1.store_cd ORDER BY T.store_cd
