SELECT count(*) FROM ma8436 left join ma1000 on ma8436.store_cd = ma1000.store_cd and #{param.businessDate} between ma1000.effective_start_date AND ma1000.effective_end_date left join ma1100 on ma8436.article_id = ma1100.article_id and #{param.businessDate} between ma1100.effective_start_date AND ma1100.effective_end_date WHERE 1 = 1 AND ma8436.store_cd = #{param.storeCd,jdbcType=VARCHAR}
AND ma8436.business_date >= #{param.startDate}
AND ma8436.business_date <= #{param.endDate}

SELECT ma8436.store_cd,ma1000.store_name,ma8436.article_id,ma1100.article_name,ma8436.business_date, ma8436.total_issued_qty FROM ma8436 left join ma1000 on ma8436.store_cd = ma1000.store_cd and #{param.businessDate} between ma1000.effective_start_date AND ma1000.effective_end_date left join ma1100 on ma8436.article_id = ma1100.article_id and #{param.businessDate} between ma1100.effective_start_date AND ma1100.effective_end_date WHERE 1 = 1 AND ma8436.store_cd = #{param.storeCd,jdbcType=VARCHAR}
AND ma8436.business_date >= #{param.startDate}
AND ma8436.business_date <= #{param.endDate}
LIMIT ${param.rows} OFFSET ${param.limitStart}

