mapper.select:
SELECT
    COALESCE(sum(pi1.bad_qty*ma2.base_order_price),0)
FROM
    pi0120 pi1,
    ma1110 ma2,
    ma1000 ma3
WHERE
    pi1.pi_cd = #{piCd}
    AND pi1.pi_date = #{piDate}
    AND pi1.store_cd = #{storeCd}
    AND ma3.store_cd = #{storeCd}
    AND ma2.article_id = pi1.article_id
    AND ma2.structure_cd = ma3.zo_cd

SELECT pi1.pi_cd, pi1.pi_date, pi1.store_cd, /*ma3.dep_cd,*/ SUM(pi2.real_time_qty) bookValQty, SUM(pi2.real_time_qty*ma4.base_sale_price) bookValAmt, count(pi1.article_id) AS sku, /*sku*/ sum(pi2.variance_qty) AS qty, /*qty*/ sum(pi2.variance_qty*ma4.base_sale_price) AS amt /*amt*/ FROM pi0120 pi1, pi0125 pi2, ma1100 ma3, ma1130 ma4, (SELECT ma2.admin_structure_cd FROM ma0020 ma2 WHERE ma2.structure_cd = (SELECT ma1.admin_structure_cd FROM ma0020 ma1 WHERE ma1.structure_cd = #{storeCd})) ma5, cm9060 cm6 WHERE pi1.pi_cd = #{piCd} AND pi1.pi_date = #{piDate} AND pi1.store_cd = #{storeCd} AND cm6.sp_id = '0000' AND pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.store_cd = pi2.store_cd AND pi1.article_id = pi2.article_id AND ma3.article_id = pi1.article_id AND ma4.article_id = ma3.article_id AND ma4.structure_cd = ma5.admin_structure_cd AND ma4.effective_start_date = ma3.effective_start_date AND cm6.sp_value BETWEEN ma3.effective_start_date and ma3.effective_end_date
AND 0 > pi2.variance_qty
AND pi2.variance_qty > 0
/*AND ma3.dep_cd BETWEEN '01' AND '02'*/
AND ma3.dep_cd = #{depCd}
GROUP BY pi1.pi_cd, pi1.pi_date, pi1.store_cd /* ,ma3.dep_cd*/

SELECT count (pi1.article_id) FROM pi0125 pi1 LEFT JOIN pi0120 pi2 ON pi1.store_cd = pi2.store_cd AND pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.article_id = pi2.article_id, ma1100 ma1, ma1130 ma2, ma1000 ma3 WHERE pi1.pi_cd = #{piCd} AND pi1.pi_date = #{piDate} AND pi1.store_cd = #{storeCd} AND pi1.article_id = ma1.article_id AND #{businessDate} BETWEEN ma1.effective_start_date AND ma1.effective_end_date /* 不盘点原材料商品 */ AND ma1.material_type != '04' and ma1.article_id not in (select article_id from pi0126) AND pi1.pi_qty IS NOT NULL AND pi1.real_time_qty <> 0 AND pi1.article_id = ma2.article_id AND #{businessDate} BETWEEN ma2.effective_start_date and ma2.effective_end_date AND ma3.store_cd = pi1.store_cd AND ma3.zo_cd = ma2.structure_cd AND #{businessDate} BETWEEN ma3.effective_start_date and ma3.effective_end_date
AND pi1.variance_qty >= #{startQty}
AND #{endQty} >= pi1.variance_qty
AND (ma2.base_sale_price*pi1.variance_qty) >= #{startAmt}
AND #{endAmt} >= (ma2.base_sale_price*pi1.variance_qty)
AND ( lower(ma1.article_id) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) OR lower(ma1.article_name) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) )

SELECT count (DISTINCT pi1.article_id) FROM pi0125 pi1 LEFT JOIN pi0120 pi2 ON pi1.store_cd = pi2.store_cd AND pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.article_id = pi2.article_id, ma1100 ma1, ma2240 ma22 left join ma1110 ma2 on ma2.article_id = ma22.article_id AND ma2.vendor_id = ma22.vendor_id AND #{businessDate} BETWEEN ma2.effective_start_date AND ma2.effective_end_date AND ma2.structure_cd = ( SELECT ma2.admin_structure_cd FROM ma0020 ma2 WHERE ma2.structure_cd = ( SELECT ma1.admin_structure_cd FROM ma0020 ma1 WHERE ma1.structure_cd = #{storeCd} ) ), ma1130 ma3, ma1000 ma4 WHERE pi1.pi_cd = #{piCd} AND pi1.pi_date = #{piDate} AND pi1.store_cd = #{storeCd} AND pi1.article_id = ma1.article_id /* 不盘点原材料商品 */ AND ma1.material_type != '04' and ma1.article_id not in (select article_id from pi0126) AND pi1.article_id = ma3.article_id AND #{businessDate} BETWEEN ma3.effective_start_date and ma3.effective_end_date AND ma4.store_cd = pi1.store_cd AND ma4.zo_cd = ma3.structure_cd AND #{businessDate} BETWEEN ma4.effective_start_date AND ma4.effective_end_date AND #{businessDate} BETWEEN ma1.effective_start_date AND ma1.effective_end_date AND pi1.pi_qty IS NULL AND ma22.article_id = ma1.article_id AND ma22.store_cd = #{storeCd}
AND pi1.variance_qty >= #{startQty}
AND #{endQty} >= pi1.variance_qty
AND (ma3.base_sale_price*pi1.variance_qty) >= #{startAmt}
AND #{endAmt} >= (ma3.base_sale_price*pi1.variance_qty)
AND ( lower(ma1.article_id) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) OR lower(ma1.article_name) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) )

SELECT count (pi1.article_id) FROM pi0125 pi1 LEFT JOIN pi0120 pi2 ON pi1.store_cd = pi2.store_cd AND pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.article_id = pi2.article_id, ma1100 ma1, ma1130 ma2, ma1000 ma3 WHERE pi1.pi_cd = #{piCd} AND pi1.pi_date = #{piDate} AND pi1.store_cd = #{storeCd} AND pi1.article_id = ma1.article_id /* 不盘点原材料商品 */ AND ma1.material_type != '04' and ma1.article_id not in (select article_id from pi0126) AND pi1.article_id = ma2.article_id AND #{businessDate} BETWEEN ma2.effective_start_date and ma2.effective_end_date AND ma3.store_cd = pi1.store_cd AND ma3.zo_cd = ma2.structure_cd AND #{businessDate} BETWEEN ma3.effective_start_date AND ma3.effective_end_date AND #{businessDate} BETWEEN ma1.effective_start_date AND ma1.effective_end_date AND pi1.pi_qty IS NOT NULL AND pi1.real_time_qty = 0
AND pi1.variance_qty >= #{startQty}
AND #{endQty} >= pi1.variance_qty
AND (ma2.base_sale_price*pi1.variance_qty) >= #{startAmt}
AND #{endAmt} >= (ma2.base_sale_price*pi1.variance_qty)
AND ( lower(ma1.article_id) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) OR lower(ma1.article_name) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) )

SELECT pi2.barcode, pi1.article_id, ma1.article_name, ma1.spec, ma1.sales_unit_id, pi1.real_time_qty AS on_hand_qty, pi1.c_qty, pi2.bad_qty, pi1.pi_qty AS second_qty, pi1.variance_qty AS variance, ma2.base_sale_price FROM pi0125 pi1 LEFT JOIN pi0120 pi2 ON pi1.store_cd = pi2.store_cd AND pi1.pi_cd = pi2.pi_cd AND pi1.pi_date = pi2.pi_date AND pi1.article_id = pi2.article_id, ma1100 ma1, ma1130 ma2, ma1000 ma3 WHERE pi1.pi_cd = #{piCd} AND pi1.pi_date = #{piDate} AND pi1.store_cd = #{storeCd} AND pi1.article_id = ma1.article_id /* 不盘点原材料商品 */ AND ma1.material_type != '04' and ma1.article_id not in (select article_id from pi0126) AND pi1.article_id = ma2.article_id AND #{businessDate} BETWEEN ma2.effective_start_date and ma2.effective_end_date AND ma3.store_cd = pi1.store_cd AND ma3.zo_cd = ma2.structure_cd AND #{businessDate} BETWEEN ma3.effective_start_date AND ma3.effective_end_date AND #{businessDate} BETWEEN ma1.effective_start_date AND ma1.effective_end_date AND pi1.pi_qty IS NOT NULL AND pi1.real_time_qty <> 0
AND pi1.variance_qty >= #{startQty}
AND #{endQty} >= (pi1.variance_qty)
AND (ma2.base_sale_price*pi1.variance_qty) >= #{startAmt}
AND #{endAmt} >= (ma2.base_sale_price*pi1.variance_qty)
AND ( lower(ma1.article_id) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) OR lower(ma1.article_name) like lower(CONCAT('%',#{searchVal,jdbcType=VARCHAR},'%')) )
ORDER BY
(ma2.base_sale_price * pi1.variance_qty) ${sord}
${sidx} ${sord}
pi1.variance_qty DESC
LIMIT ${rows} OFFSET ${limit}

