select pi_cd, pi_date, store_cd, article_id, article_name, barcode, sales_unit_id, spec, pi_qty_status, first_qty as second_qty, stocktake_time, bad_qty, converted, region from pi0120_t where pi_cd = #{piCd} AND pi_date = #{piDate} AND store_cd = #{storeCd}
