    )

SELECT DISTINCT A.pi_date, A.pi_cd, A.pi_start_time, A.pi_end_time, B.store_cd, B.store_name, A.review_status, A.pi_status AS pi_status_cd, D.code_name AS pi_status_name, E.vouch_name AS voucher_type_name, A.remarks, oper.emp_name AS create_user_id, concat(A.create_ymd,A.create_hms) AS create_ymd FROM PI0100 A LEFT JOIN MA4200 oper ON oper.emp_num_id = A.create_user_id, MA1000 B, CM9060 C, CM9010 D, CM9100 E WHERE 1=1 AND C.sp_id = '0000' AND D.code_type = '00530' AND E.vouch_type_user = '605' AND A.store_cd = B.store_cd AND A.pi_status != '01' AND C.sp_value BETWEEN B.effective_start_date AND B.effective_end_date AND A.pi_status = D.code_value AND A.store_cd IN
#{store,jdbcType=VARCHAR}
AND lower(A.pi_cd) like lower(CONCAT('%',#{pi0100Param.piCd},'%'))
AND A.pi_status = #{pi0100Param.piStatus}
AND A.review_status = CAST (#{pi0100Param.reviewStatus} AS NUMERIC )
AND A.pi_date BETWEEN #{pi0100Param.piStartDate} AND #{pi0100Param.piEndDate}
ORDER BY A.pi_date DESC
LIMIT ${pi0100Param.rows} OFFSET ${pi0100Param.limitStart}

