select distinct t_backlog.c_enable status, t_backlog.record_cd, t_backlog.n_typeid type_id, t_audit_type.c_type_name type_name, audit_tab.audit_time,/*发起时间*/ ma4200.emp_num_id create_user_id,/*发起人id*/ ma4200.emp_name create_user_name,/*发起人name*/ t_backlog.c_url url, t_backlog.n_recordid id from t_backlog, t_audit_type, ma4200, (select t_audit_tab.record_cd, t_audit_tab.audit_time, t_audit_tab.n_operatorid, t_audit_tab.n_typeid from t_audit_tab where t_audit_tab.c_enable = '1' and t_audit_tab.sub_no = 0 ) audit_tab where t_backlog.c_enable = '1' and t_backlog.n_typeid = t_audit_type.n_typeid and t_backlog.n_typeid = audit_tab.n_typeid and t_backlog.record_cd = audit_tab.record_cd and ma4200.emp_num_id = audit_tab.n_operatorid AND t_backlog.n_roleid||t_backlog.store_cd IN ( select position||store_cd from ma4210 where user_code = #{operatorId} union select CASE ma4160.position WHEN 5 THEN '5000000' ELSE position||store_cd END from ma4200,ma4160 where ma4200.emp_num_id = #{operatorId} and ma4200.job_type_cd = ma4160.job_type_cd )
and to_char(audit_tab.audit_time, 'yyyymmdd') >= #{startDate}
and #{endDate} >= to_char(audit_tab.audit_time, 'yyyymmdd')
and t_backlog.n_typeid = #{typeId,jdbcType=INTEGER}
and lower(ma4200.emp_num_id) LIKE CONCAT('%',lower(#{userCode}),'%')
and lower(ma4200.emp_name) LIKE CONCAT('%',lower(#{userName}),'%')
order by audit_time desc
limit ${limitEnd} offset ${limitStart}

