mapper.select:
SELECT emp_num_id, emp_id, ma4200.store_cd,ma1000.store_name, ma4200.job_type_cd,ma4160.job_catagory_name, effective_status, emp_name, emp_gender, emp_country, telephone_no, mobile_phone_no, emp_birthdate, emp_education, emp_date, emp_leave_date, emp_address, emp_postal_code, emp_comment, emp_email, md, (select code_name from cm9010 where cm9010.code_type='00270' and cm9010.code_value=ma4200.emp_gender ) as emp_gender_name, (select code_name from cm9010 where cm9010.code_type='00220' and cm9010.code_value=ma4200.effective_status ) as effective_status_name FROM ma4200 left join ma1000 on ma4200.store_cd=ma1000.store_cd and #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date left join ma4160 on ma4200.job_type_cd = ma4160.job_type_cd
AND lower(emp_num_id) like concat('%',lower(#{empNumId,jdbcType=VARCHAR}),'%')
AND lower(emp_name) like concat('%',lower(#{empName,jdbcType=VARCHAR}),'%')
AND ma4200.store_cd=#{storeCd,jdbcType=VARCHAR}
AND ma4200.job_type_cd=#{jobTypeCd,jdbcType=VARCHAR}
AND ma4200.effective_status=#{effectiveStatus,jdbcType=CHAR}
ORDER BY emp_num_id desc
limit ${limitEnd} offset ${limitStart}

SELECT count(*) FROM ma4200 left join ma1000 on ma4200.store_cd=ma1000.store_cd and #{businessDate,jdbcType=VARCHAR} BETWEEN effective_start_date AND effective_end_date left join ma4160 on ma4200.job_type_cd = ma4160.job_type_cd
AND emp_num_id like concat('%',#{empNumId,jdbcType=VARCHAR},'%')
AND emp_name like concat('%',#{empName,jdbcType=VARCHAR},'%')
AND ma4200.store_cd=#{storeCd,jdbcType=VARCHAR}
AND ma4200.job_type_cd=#{jobTypeCd,jdbcType=VARCHAR}
AND ma4200.effective_status=#{effectiveStatus,jdbcType=CHAR}

mapper.update:
update ma4200 set store_cd = #{storeCd,jdbcType=VARCHAR}, job_type_cd = #{jobTypeCd,jdbcType=VARCHAR}, effective_status = #{effectiveStatus,jdbcType=CHAR}, emp_name = #{empName,jdbcType=VARCHAR}, emp_gender = #{empGender,jdbcType=CHAR}, emp_country = #{empCountry,jdbcType=VARCHAR}, telephone_no = #{telephoneNo,jdbcType=VARCHAR}, mobile_phone_no = #{mobilePhoneNo,jdbcType=VARCHAR}, emp_birthdate = #{empBirthdate,jdbcType=VARCHAR}, emp_education = #{empEducation,jdbcType=VARCHAR}, emp_date = #{empDate,jdbcType=VARCHAR}, emp_leave_date = #{empLeaveDate,jdbcType=VARCHAR}, emp_address = #{empAddress,jdbcType=VARCHAR}, emp_postal_code = #{empPostalCode,jdbcType=VARCHAR}, emp_comment = #{empComment,jdbcType=VARCHAR}, emp_email = #{empEmail,jdbcType=CHAR}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_ymd = #{updateYmd,jdbcType=CHAR}, update_hms = #{updateHms,jdbcType=CHAR}
AND emp_id=#{empId,jdbcType=INTEGER}
AND emp_num_id=#{empNumId,jdbcType=VARCHAR}

update ma4200
set
effective_status = '90',
update_user_id = #{updateUserId,jdbcType=VARCHAR},
update_ymd = #{updateYmd,jdbcType=CHAR},
update_hms = #{updateHms,jdbcType=CHAR}

  AND emp_id=#{empId,jdbcType=INTEGER}

    AND emp_num_id=#{empNumId,jdbcType=VARCHAR}

update ma4200
set
emp_password = #{empNewPassword,jdbcType=VARCHAR},
update_user_id = #{updateUserId,jdbcType=VARCHAR},
update_ymd = #{updateYmd,jdbcType=CHAR},
update_hms = #{updateHms,jdbcType=CHAR}

    AND emp_num_id=#{empNumId,jdbcType=VARCHAR}
    AND emp_password=#{empPassword,jdbcType=VARCHAR}
