mapper.select:
SELECT T.*, T1.emp_name AS update_user_name FROM ( select pay_cd,pay_name,pay_name_print,interface_sts,interface_address, update_user_id, update_ymd||update_hms as update_date,review_status from sa0000
AND lower(pay_cd) like lower(CONCAT('%',#{payCd},'%'))
AND lower(pay_name) like lower(CONCAT('%',#{payName},'%'))
AND review_status = #{reviewStatus,jdbcType=INTEGER}
) T LEFT JOIN ( SELECT emp_num_id, emp_name FROM ma4200 )T1 ON T.update_user_id = T1.emp_num_id
ORDER BY T.pay_cd
limit ${limitEnd} offset ${limitStart}

select pay_cd, store_cd, pos_id, pos_display,record_status, (select code_name from cm9010 where cm9010.code_type='00385' and cm9010.code_value=sa0005.pos_display ) as pos_display_name, (select store_name from ma1000 where
ma1000.effective_start_date <= #{businessDate,jdbcType=VARCHAR} and ma1000.effective_end_date >= #{businessDate,jdbcType=VARCHAR}
and ma1000.store_cd = sa0005.store_cd) as store_name, (select pos_name from sa0160 where sa0160.store_cd= sa0005.store_cd and sa0160.pos_id= sa0005.pos_id ) as pos_name from sa0005 where
pay_cd = #{payCd,jdbcType=VARCHAR}
order by sa0005.store_cd,sa0005.pos_id

select pay_cd, sa0005.store_cd, pos_id, pos_display,sa0005.create_user_id,sa0005.create_ymd,sa0005.create_hms,MA4200.emp_name, (select code_name from cm9010 where cm9010.code_type='00385' and cm9010.code_value=sa0005.pos_display ) as pos_display_name, (select store_name from ma1000 where
ma1000.effective_start_date <= #{businessDate,jdbcType=VARCHAR} and ma1000.effective_end_date >= #{businessDate,jdbcType=VARCHAR}
and ma1000.store_cd = sa0005.store_cd) as store_name, (select pos_name from sa0160 where sa0160.store_cd= sa0005.store_cd and sa0160.pos_id= sa0005.pos_id ) as pos_name from sa0005_delete_record sa0005,ma4200 where
pay_cd = #{payCd,jdbcType=VARCHAR}
AND sa0005.create_user_id=MA4200.emp_num_id order by sa0005.store_cd,sa0005.pos_id

select distinct pay_cd, sa0005.store_cd, pos_id, pos_display,sa0005.create_user_id,sa0005.create_ymd,sa0005.create_hms,sa0005.record_status, (select code_name from cm9010 where cm9010.code_type='00385' and cm9010.code_value=sa0005.pos_display ) as pos_display_name, (select store_name from ma1000 where
ma1000.effective_start_date <= #{businessDate,jdbcType=VARCHAR} and ma1000.effective_end_date >= #{businessDate,jdbcType=VARCHAR}
and ma1000.store_cd = sa0005.store_cd) as store_name, (select pos_name from sa0160 where sa0160.store_cd= sa0005.store_cd and sa0160.pos_id= sa0005.pos_id ) as pos_name from sa0005_delete_record sa0005,ma4200 where 1=1
AND pay_cd = #{payCd,jdbcType=VARCHAR}
order by sa0005.store_cd,sa0005.pos_id

 select
 pay_cd, store_cd, pos_id, pos_display,
 (select code_name from cm9010
 where cm9010.code_type='00385'
 and cm9010.code_value=sa0005.pos_display
 ) as pos_display_name,
 (select store_name from ma1000
 where

     ma1000.effective_start_date <= #{businessDate,jdbcType=VARCHAR}
     and ma1000.effective_end_date >= #{businessDate,jdbcType=VARCHAR}

 and ma1000.store_cd = sa0005.store_cd) as store_name,
 (select pos_name from sa0160
 where sa0160.store_cd= sa0005.store_cd
 and sa0160.pos_id= sa0005.pos_id
 ) as pos_name,
 (select sa0000.pay_name  from sa0000 where sa0000.pay_cd=sa0005.pay_cd
) AS pay_name
 from sa0005


     sa0005.store_cd = #{storeCd,jdbcType=VARCHAR}


 order by sa0005.store_cd,sa0005.pos_id
