SELECT DISTINCT ma10.store_cd, ma10.store_name, sa02.sale_date, sa02.price_actual, sa02.sale_qty, sa02.sale_amount, ma11.article_id, ma11.article_name, ma07.dep_cd, ma08.pma_cd, ma09.category_cd, ma01.sub_category_cd, concat(ma07.dep_cd,' ',ma07.dep_name) dep_name, concat(ma08.pma_cd,' ',ma08.pma_name) pma_name, concat(ma09.category_cd,' ',ma09.category_name) category_name, concat(ma01.sub_category_cd,' ',ma01.sub_category_name) sub_category_name, oper.c_operatorcode amCd, oper.c_operatorname amName, sa02.barcode, sa02.acc_date FROM ma1000 ma10, sa0020 sa02, ma1100 ma11, ma0070 ma07, ma0080 ma08, ma0090 ma09, ma0100 ma01, t_operator oper where ma10.store_cd=sa02.store_cd and ma10.ofc = oper.c_operatorcode and (SELECT C.sp_value FROM CM9060 C WHERE C.sp_id = '0000') BETWEEN ma11.effective_start_date AND ma11.effective_end_date and (SELECT C.sp_value FROM CM9060 C WHERE C.sp_id = '0000') BETWEEN ma10.effective_start_date AND ma10.effective_end_date AND sa02.article_id=ma11.article_id AND ma11.pma_cd=ma08.pma_cd AND MA07.dep_cd=ma08.dep_cd AND ma11.category_cd = ma09.category_cd AND ma11.sub_category_cd = ma01.sub_category_cd AND ma08.effective_sts = '10' AND ma11.product_status = '10' AND ma10.store_cd IN
#{store,jdbcType=VARCHAR}
AND to_char(sa02.sale_date,'yyyyMMdd') >= #{goodSaleReport.startDate}
AND to_char(sa02.sale_date,'yyyyMMdd') <= #{goodSaleReport.endDate}
AND ma11.dep_cd = #{goodSaleReport.depCd}
AND sa02.store_cd = #{goodSaleReport.storeCd}
AND ma11.pma_cd = #{goodSaleReport.pmaCd}
AND ma11.category_cd = #{goodSaleReport.categoryCd}
AND ma11.sub_category_cd = #{goodSaleReport.subCategoryCd}
AND ma10.zo_cd = #{goodSaleReport.city}
AND ma10.ofc = #{goodSaleReport.am}
order by sa02.sale_amount DESC
LIMIT ${goodSaleReport.rows} OFFSET ${goodSaleReport.limitStart}

